module.exports = async (params,config) => {
    const { userId, password } = params;
    debugger;
    const result = await app.call.validateHashPassword({password,userId});
    if(result.success){
        const user = await page.query.findUsers({filter:{USER_ID_ : {
                type : 'equal',
                value : userId
            }}})
            if(user.data && user.data.length > 0){
                utils.setItem('app-user',user.data[0]);
                app.var.loggedInUser.set(user.data[0]);
            }
    }else{
        config.errors.set({password:result.message,userId:result.message});
    }
}

variables.functionCode:
const downloadStatus = page.var.downloadStatus.get();
const MAX_ATTEMPT = 100;
(async () => {
    if (downloadStatus.status.toLocaleLowerCase() === 'started') {
        let attemp = 0
        let isReady = false;
        while (!isReady && attemp < MAX_ATTEMPT) {
            const readyUnknown = await checkIfDownloadIsReady();
            isReady = utils.toBoolean(readyUnknown)
            attemp++;
            log.debug('attempt',attemp,'downloadIsReady ?','readyUnknown',readyUnknown,'isReady',isReady)
        }
        if (isReady) {
            await downloadTheFile(downloadStatus.attachmentId);
            navigate('app/authenticate');
        }
    }

})();

function checkIfDownloadIsReady() {
    return new Promise(resolve => {
        setTimeout(() => {
            fetch(`http://localhost:5008/EsnaadMAppFacade/CheckDownloadProgress?downloadId=${downloadStatus.attachmentId}`, { credentials: 'include' }).then(response => {
                if (!response.ok) {
                    resolve(false);
                } else {
                    response.json().then((json) => {
                        if (json.Success) {
                            resolve(json.Result.Status === 'Completed')
                            return;
                        }
                        resolve(false);
                    })
                }
            }).catch(err => {
                resolve(false);
            })
        }, 1000);
    })
}

async function downloadTheFile(attachmentId) {
    try {
        const response = await fetch(`http://localhost:5008/home/DownloadEsnaadMData?downloadId=${attachmentId}`, { credentials: 'include' });
        if (!response.ok) {
            alert('Error' + response.statusText);
        }
        const blob = await response.blob();
        const arrayBuffer = await blob.arrayBuffer();
        await tools.saveSqlLite(arrayBuffer);
        utils.setItem('db-file-name', attachmentId);
        const tailNumber = utils.getItem('tail-number');
        await populateOutgoingTableFaults(tailNumber);
        await populateOutgoingTableMissions(tailNumber);
        await populateOutgoingTableInspections(tailNumber);
        await db.commit();
        await alertBox({
            message: `The aircraft with tail number ${tailNumber} has been downloaded into the local database and is ready to use in offline mode.`,
        })
        app.var.tailNumber.set(tailNumber);
    } catch (error) {
        alertBox({
            message: `Error downloading the file ${error}`,
        })
    }
}

async function populateOutgoingTableMissions(tailNo) {
    const { data } = await app.query.getTailBattalionAndCommandFromTailNumber({ params: { tailNumber: tailNo } });
    if (data.length === 0) {
        return;
    }
    const unitId = data[0].TAIL_UNIT_ID_
    const missions = await db.read('OPS_TRN_MISSIONS', {});
    for (const mission of missions) {
        await db.record('MISSIONS', {
            ABORT_REASON: mission.ABORT_REASON,
            ABORT_REASON_ID: mission.ABORT_REASON_ID,
            ACCESS_CODES_: mission.ACCESS_CODES_,
            CALL_ID: mission.CALL_ID,
            CANCEL_REASON: mission.CANCEL_REASON,
            CANCEL_REASON_ID: mission.CANCEL_REASON_ID,
            CANCEL_REASON_RMKS_: mission.CANCEL_REASON_RMKS_,
            CREATED_BY_: mission.CREATED_BY_,
            CREATED_ON_: mission.CREATED_ON_,
            DATE_: mission.DATE_,
            DESCRIPTION_: mission.DESCRIPTION_,
            EXTERNAL_ID: mission.EXTERNAL_ID,
            MISSION_ID_: mission.ID_,
            MISSION_NO_: mission.MISSION_NO_,
            MISS_PLAN_TYPE_: mission.MISS_PLAN_TYPE_,
            MISS_SUB_TYPE_ID: mission.MISS_SUB_TYPE_ID,
            MOD_BY_: mission.MOD_BY_,
            MOD_ON_: mission.MOD_ON_,
            PLTF_CFG_FUEL_UOM_ID: mission.PLTF_CFG_FUEL_UOM_ID,
            PLTF_CFG_ID: mission.PLTF_CFG_ID,
            PLTF_CFG_MNT_REM_: mission.PLTF_CFG_MNT_REM_,
            PLTF_CFG_NO_OF_PASS_: mission.PLTF_CFG_NO_OF_PASS_,
            PLTF_CFG_REQ_FUEL_: mission.PLTF_CFG_REQ_FUEL_,
            ROUTE_: mission.ROUTE_,
            SUBMT_BY_: mission.SUBMT_BY_,
            SUBMT_ON_: mission.SUBMT_ON_,
            SUPPORT_SUB_UNIT_ID: mission.SUPPORT_SUB_UNIT_ID,
            TERM_: mission.TERM_,
            UNIT_ID_: unitId
        });
    }

    const wings = await db.read('OPS_TRN_WINGS', {});
    for (const wing of wings) {
        await db.record('WINGS', {
            COY_ID: wing.COY_ID,
            IDX_: wing.IDX_,
            MISSION_ID_: wing.MISSION_ID,
            MISSION_WING_ID_: wing.ID_,
            PLTF_ID: wing.PLTF_ID,
            UNIT_ID_: unitId
        })
    }

    const flights = await db.read('OPS_TRN_FLIGHTS', {});
    for (const flight of flights) {
        await db.record('FLIGHTS', {
            BASE_FROM_ID: flight.BASE_FROM_ID,
            BASE_TO_ID: flight.BASE_TO_ID,
            CREATED_BY_: flight.CREATED_BY_,
            CREATED_ON_: flight.CREATED_ON_,
            ETA_: flight.ETA_,
            ETD_: flight.ETD_,
            IDX_: flight.IDX_,
            MISSION_FLIGHT_ID_: flight.ID_,
            MISSION_ID_: flight.MISSION_ID,
            MOD_BY_: flight.MOD_BY_,
            MOD_ON_: flight.MOD_ON_,
            TERM_: flight.TERM_,
            UNIT_ID_: unitId
        });
    }

    const tailCrews = await db.read('OPS_TRN_TAIL_CREWS', {});
    for (const tailCrew of tailCrews) {
        const wing = await db.find('OPS_TRN_WINGS', { ID_: tailCrew.WING_ID });
        await db.record('TAIL_CREWS', {
            ASSIGNED_: tailCrew.ASSIGNED_,
            IS_ACTUAL_TAIL_ASSIGNED_: tailCrew.IS_ACTUAL_TAIL_ASSIGNED_,
            MISSION_ID_: wing.MISSION_ID,
            MISSION_TAIL_CREW_ID_: tailCrew.ID_,
            PLANNED_TAIL_ID: tailCrew.PLANNED_TAIL_ID,
            TAIL_ID: tailCrew.TAIL_ID,
            UNIT_ID_: unitId,
            WING_ID: tailCrew.WING_ID
        })
    }

    const missionCrews = await db.read('OPS_TRN_MISSION_CREW_PLAN', {});
    for (const missionCrew of missionCrews) {
        const tailCrew = await db.find('OPS_TRN_TAIL_CREWS', { ID_: missionCrew.TAIL_CREW_ID });
        const wing = await db.find('OPS_TRN_WINGS', { ID_: tailCrew.WING_ID });

        await db.record('MISSION_CREW_PLANS', {
            DUTY_SYMBOL_ID: missionCrew.DUTY_SYMBOL_ID,
            FLIGHT_CREW_ID: missionCrew.FLIGHT_CREW_ID,
            MISSION_CREW_PLAN_ID_: missionCrew.ID_,
            MISSION_ID_: wing.MISSION_ID,
            SEAT_CONFIGURATION_ID_: missionCrew.SEAT_CONFIGURATION_ID_,
            TAIL_CREW_ID: missionCrew.TAIL_CREW_ID,
            UNIT_ID_: unitId
        })
    }

    const tailFlights = await db.read('OPS_TRN_TAIL_FLIGHT', {});
    for (const tailFlight of tailFlights) {
        const tailCrew = await db.find('OPS_TRN_TAIL_CREWS', { ID_: tailFlight.TAIL_CREW_ID });
        const wing = await db.find('OPS_TRN_WINGS', { ID_: tailCrew.WING_ID });
        await db.record('TAIL_FLIGHTS', {
            ACTUAL_BASE_FROM_ID: tailFlight.ACTUAL_BASE_FROM_ID,
            ACTUAL_BASE_TO_ID: tailFlight.ACTUAL_BASE_TO_ID,
            ATA_: tailFlight.ATA_,
            ATD_: tailFlight.ATD_,
            CANCELLATION_REASON: tailFlight.CANCELLATION_REASON,
            CANCELLATION_REASON_ID: tailFlight.CANCELLATION_REASON_ID,
            CANCELLATION_REASON_RMKS_: tailFlight.CANCELLATION_REASON_RMKS_,
            CRANK_TIME_: tailFlight.CRANK_TIME_,
            FLIGHT_ID: tailFlight.FLIGHT_ID,
            FLIGHT_STATUS_: tailFlight.FLIGHT_STATUS_,
            IFFCODE_: tailFlight.IFFCODE_,
            MISSION_ID_: wing.MISSION_ID,
            TAIL_CREW_ID: tailFlight.TAIL_CREW_ID,
            TAIL_FLIGHT_ID_: tailFlight.ID_,
            UNIT_ID_: unitId
        })
    }

    const tailFlightRecords = await db.read('OPS_TRN_TAIL_FLIGHT_RECORD', {});
    for (const tailFlightRecord of tailFlightRecords) {
        const tailFlight = await db.find('OPS_TRN_TAIL_FLIGHT', { ID_: tailFlightRecord.TAIL_FLIGHT_ID });
        const tailCrew = await db.find('OPS_TRN_TAIL_CREWS', { ID_: tailFlight.TAIL_CREW_ID });
        const wing = await db.find('OPS_TRN_WINGS', { ID_: tailCrew.WING_ID });

        await db.record('TAIL_FLIGHT_RECORDS', {
            CREATED_BY_: tailFlightRecord.CREATED_BY_,
            CREATED_ON_: tailFlightRecord.CREATED_ON_,
            CYCLES_: tailFlightRecord.CYCLES_,
            ENGINE_STARTS1_: tailFlightRecord.ENGINE_STARTS1_,
            ENGINE_STARTS2_: tailFlightRecord.ENGINE_STARTS2_,
            EXTERNAL_LOAD_: tailFlightRecord.EXTERNAL_LOAD_,
            FLIGHT_MINS_: tailFlightRecord.FLIGHT_MINS_,
            FULL_NAME_CREATED_BY_: tailFlightRecord.FULL_NAME_CREATED_BY_,
            FULL_NAME_MOD_BY_: tailFlightRecord.FULL_NAME_MOD_BY_,
            INTERNAL_LOAD_: tailFlightRecord.INTERNAL_LOAD_,
            LANDINGS_: tailFlightRecord.LANDINGS_,
            MISSION_CODE_: tailFlightRecord.MISSION_CODE_,
            MISSION_ID_: wing.MISSION_ID,
            MISSION_TAIL_FLIGHT_RECORD_ID_: tailFlightRecord.ID_,
            MOD_BY_: tailFlightRecord.MOD_BY_,
            MOD_ON_: tailFlightRecord.MOD_ON_,
            PASSENGERS_: tailFlightRecord.PASSENGERS_,
            PID_CREATED_BY_: tailFlightRecord.PID_CREATED_BY_,
            PID_MOD_BY_: tailFlightRecord.PID_MOD_BY_,
            ROUNDS_127_NAG_: tailFlightRecord.ROUNDS_127_NAG_,
            ROUNDS_20_: tailFlightRecord.ROUNDS_20_,
            ROUNDS_20_NAG_: tailFlightRecord.ROUNDS_20_NAG_,
            ROUNDS_30_: tailFlightRecord.ROUNDS_30_,
            ROUNDS_40_: tailFlightRecord.ROUNDS_40_,
            ROUNDS_762_: tailFlightRecord.ROUNDS_762_,
            ROUNDS_AM39_NAG_: tailFlightRecord.ROUNDS_AM39_NAG_,
            ROUNDS_AS15TT_NAG_: tailFlightRecord.ROUNDS_AS15TT_NAG_,
            ROUNDS_HELLFIRE_: tailFlightRecord.ROUNDS_HELLFIRE_,
            ROUNDS_ROCKET_: tailFlightRecord.ROUNDS_ROCKET_,
            ROUNDS_TORPEDO_NAG_: tailFlightRecord.ROUNDS_TORPEDO_NAG_,
            ROUNDS_TOW_: tailFlightRecord.ROUNDS_TOW_,
            RTA_: tailFlightRecord.RTA_,
            RTD_: tailFlightRecord.RTD_,
            SNAPSHOT_PLTF_CFG_ID_: tailFlightRecord.SNAPSHOT_PLTF_CFG_ID_,
            TAAMS_FLIGHT_MINS_: tailFlightRecord.TAAMS_FLIGHT_MINS_,
            TAIL_FLIGHT_ID: tailFlightRecord.TAIL_FLIGHT_ID,
            TERM_: tailFlightRecord.TERM_,
            UNIT_ID_: unitId,
            VIP_CODE_ID_: tailFlightRecord.VIP_CODE_ID_
        })
    }
}

async function populateOutgoingTableInspections(tailNo) {
    const { data } = await app.query.getTailBattalionAndCommandFromTailNumber({ params: { tailNumber: tailNo } });
