async function downloadTail ({ tail }){
    const result = await page.fetch.download_tail({ downloadEntries: [{ "Module": "ESNAAD_M" }, { "Module": "MIS_FM_SI", "Params": [tail] }] });
    const { Result, error, ErrorMessage, ErrorCode } = result;
    
    if (ErrorCode === 'ADM.002.001') {
        const action = await alertBox({
            message: `Session is expired, or invalid permission`,
            buttons : [{
                id : 'login',
                label : 'I would like to go back to login page',
                icon : 'IoIosLogIn'
            },{
                id : 'retry',
                label : 'Please retry again',
                icon : 'IoMdRefresh'
            }]
        })
        if(action === 'login'){
            return navigate('app/login');
        }
        if(action === 'retry'){
            return await downloadTail({tail})
        }
    }
    if (error || ErrorMessage) {
        await alertBox({
            message: `Error ${error}`
        })
        return;
    }
    page.var.downloadStatus.set({
        attachmentId: Result.DownloadId,
        status: Result.Status
    })
}

module.exports = downloadTail

