async function login({ userId, password }) {
    const response = await page.fetch.loginAuthenticate({ password, userId });
    if (response.error) {
        await alertBox({
            message: `Unable to reach server ${response.error}`
        });
        return;
    }
    if (response.ErrorCode) {
        if (response.ErrorCode === 'ADM.001.004') {
            await alertBox({
                message: 'Invalid login userId or Password.'
            });
            return;
        }
        if (response.ErrorCode === 'ADM.001.008') {
            const result = await alertBox({
                message: 'Session is already active, should we force login ?',
                buttons: [{
                    id: 'yes',
                    label: 'Yes force login and logout session in other device',
                    icon: 'IoMdLogIn'
                }, {
                    id: 'no',
                    label: 'No, cancel my action',
                    icon: 'IoIosLogOut'
                }]
            });
            if (result === 'yes') {
                await page.fetch.forceLogout({ userId });
                return await login({ userId, password });
            }
        }
        return;
    }
    const { FirstName, MiddleName, LastName } = response.Result;
    return { FirstName, MiddleName, LastName };
}


module.exports = async (params) => {
    const result = await login({password:params.password,userId:params.userId});
    if (result) {
        // here we are navigateing 
        const { FirstName, MiddleName, LastName } = result;
        const name = [FirstName, MiddleName, LastName];
        navigate('app/download')
    }
};

