async function populateFault(faultNo) {
    const result = await page.query.faultUnitList({ filter: { FAULT_NO_: { type: 'equal', value: faultNo } } })
    if (!hasValue(result)) {
        return;
    }
    const faultInfo = result.data[0];
    page.var.faultUnit.set(faultInfo);
    const result2 = await page.query.faultCorrectList({ filter: { FAULT_UNIT_ID_: { type: 'equal', value: faultInfo.FAULT_UNIT_ID_ } } });
    if (!hasValue(result2)) {
        return;
    }
    const faultCorrect = result2.data[0];
    page.var.faultCorrect.set(faultCorrect);

    const result3 = await page.query.faultCorrectingItems({ filter: { FAULT_CORRECT_ID_: { type: 'equal', value: faultCorrect.FAULT_CORRECT_ID_ } } })
    if (hasValue(result3)) {
        page.var.faultCorrectingItems.set(result3.data);
    }
    page.var.hasNlt.set(!utils.isEmpty(faultInfo.FAULT_INFO_NLT_DATE_))
    page.var.formState.set('view');

