async function generateFaultNo() {
    const tailNo = app.var.appContext.get()?.tail?.name;
    const faultUnitCount = (await db.query('select count(*) totalFaults from FAULT_UNITS', {}))[0];
    let totalFaults = utils.toNumber(faultUnitCount.totalFaults);
    const fourDigitYear = utils.startPad(new Date().getFullYear(), 4);
    const fourDigitTail = utils.startPad(tailNo, 4);
    const threeDigitTotalFault = utils.startPad(++totalFaults, 3);
    const faultNo = `AF${fourDigitYear}${fourDigitTail}${threeDigitTotalFault}`;
    return faultNo;
}

if (faultNo) {
    populateFault(faultNo)
} else {
    createNewFault();
}

