const data = page.var.faultCorrectingItems.get()
module.exports = async () => {
    const gridData = [];
    for(const d of data){
        let summarize = gridData.find(gd => gd.USR_ID === d.USR_ID && gd.CAT_ID === d.CAT_ID);
        if(utils.isEmpty(summarize)){
            summarize = {USR_ID:d.USR_ID,CAT_ID:d.CAT_ID,MNT_MAIN_HRS:d.MNT_MAIN_HRS,TEMP:'',CORRECTING_ITEM_IDS:d.FAULT_CORRECTING_ITEM_ID_.toString()};
            gridData.push(summarize);
        }else{
            summarize.MNT_MAIN_HRS += d.MNT_MAIN_HRS
            summarize.CORRECTING_ITEM_IDS += ','+d.FAULT_CORRECTING_ITEM_ID_
