module.exports = async (value) => {
    if(value.ACC_STS_ID === undefined || value.ACC_STS_ID === null){
        value.ACC_STS_ID = value.FAULT_INFO_STS_ID;
    }
    db.updateRecord('FAULT_UNITS',value,'FAULT_UNIT_ID_');
    db.updateRecord('FAULT_CORRECTS',page.var.faultCorrect.get(),'FAULT_CORRECT_ID_');
    await db.commit();
    page.var.formState.set('view');
    page.var.faultUnit.set(value);
}

module.exports = async value => {
    await db.updateRecord('FAULT_CORRECTS', value, 'FAULT_CORRECT_ID_');
    const correctingItems = page.var.faultCorrectingItems.get();

    const result = await db.read('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.STS' }, { ORDER_: 'asc' });
    const highRiskStatus = result.filter((_, index) => index > 1).map(i => i.ID_);

    let allCorrectingItemsAreClosed = correctingItems.length > 0;
    
    for (const correctingItem of correctingItems) {
        await db.updateRecord('FAULT_CORRECTING_ITEMS', correctingItem, 'FAULT_CORRECTING_ITEM_ID_');
        const hasUserId = !utils.isEmpty(correctingItem.USR_ID);
        const hasCatId = !utils.isEmpty(correctingItem.CAT_ID);
        const hasHours = correctingItem.MNT_MAIN_HRS > 0;
        const isHighRisk = highRiskStatus.indexOf(correctingItem.STS_ID) >= 0;
        const hasTi = !utils.isEmpty(correctingItem.TI_USR_ID);
        const isClosed = hasUserId && hasCatId && hasHours && (isHighRisk ? hasTi : true);
        if (!isClosed) {
            allCorrectingItemsAreClosed = false;
        }
    }
    const isHighRisk = highRiskStatus.indexOf(page.var.faultUnit.get().ACC_STS_ID) >= 0;
    if (allCorrectingItemsAreClosed) {
        const faultUnit = { ...page.var.faultUnit.get() };
        
        if (isHighRisk) {
            faultUnit.FAULT_STS_ = 'Open';
            faultUnit.FAULT_STATE_ = 'Validating';
        } else {
            faultUnit.CLOSE_DATE_ = utils.dateToString(new Date());
            faultUnit.FAULT_STS_ = 'Closed';
            faultUnit.FAULT_STATE_ = 'Finished';
        }
        await db.updateRecord('FAULT_UNITS', faultUnit, 'FAULT_UNIT_ID_');
        if(faultUnit.FAULT_STS_ === 'Closed'){
            await app.call.updateInspectionWhenFaultClosed({faultUnitId:faultUnit.FAULT_UNIT_ID_});
        }
        page.var.faultUnit.set(faultUnit);
    }
    await db.commit();
    page.var.formState.set('view');
}

module.exports = async val => {
    val.CLOSE_DATE_ = utils.dateToString(new Date());
    val.FAULT_STS_ = 'Closed';
    val.FAULT_STATE_ = 'Finished';
    await db.updateRecord('FAULT_UNITS', val, 'FAULT_UNIT_ID_');
    const faultUnit = page.var.faultUnit.get();
    if (val.FAULT_STS_ === 'Closed') {
        await app.call.updateInspectionWhenFaultClosed({ faultUnitId: faultUnit.FAULT_UNIT_ID_ });
    }
    db.commit();
    page.var.formState.set('view')
}

