module.exports = () => {
    const faultCorrect = {...page.var.faultCorrect.get()};
    const {cycles,hours,landings,recordedHours,recordedCycles,recordedLandings} = app.var.appContext.get().tailStatus;
    const totalHours = hours + recordedHours;
    const totalCycles = cycles + recordedCycles;
    const totalLandings = landings + recordedLandings;
    let faultCorrectUpdated = false;
    if(utils.isEmpty(faultCorrect.AIRCRAFT_HR_)){
        faultCorrect.AIRCRAFT_HR_ = utils.toNumber((totalHours / 60).toFixed(1));
        faultCorrectUpdated = true;
    }
    if(utils.isEmpty(faultCorrect.AIRCRAFT_CYCLES_)){
        faultCorrect.AIRCRAFT_CYCLES_ = totalCycles;
        faultCorrectUpdated = true;
    }
    if(utils.isEmpty(faultCorrect.AIRCRAFT_LANDINGS_)){
        faultCorrect.AIRCRAFT_LANDINGS_ = totalLandings;
        faultCorrectUpdated = true;
    }
    if(faultCorrectUpdated){
        page.var.faultCorrect.set(faultCorrect);
    }
    page.var.formState.set('editFaultCorrection');
}

