module.exports = async () => {
    const rosterId = await navigatePanel('mnt/fault/recorded-fault-detail-13-2-mme');
    const faultUnitId = page.var.faultUnit.get().FAULT_UNIT_ID_;
    if (rosterId === false) {
        return;
    }
    const mmeActions = await db.read('MNT_MST_MME_ACTIONS', { ROSTER_ID: utils.toNumber(rosterId) },{ORDER_:'asc'});
    if (mmeActions) {
        const loggedInUser = app.var.loggedInUser.get();
        const correct = await db.find('FAULT_CORRECTS', { FAULT_UNIT_ID_: faultUnitId });
        const actions = await db.read('FAULT_CORRECTING_ITEMS', { FAULT_UNIT_ID_: faultUnitId });
        const linkedActions = actions.filter(i => !utils.isEmpty(i.GROUP_NO_));
        const hasAlreadyMMELinked = linkedActions.length > 0;
        if (hasAlreadyMMELinked) {
            const response = await alertBox({
                message: `It appears that actions have already been assigned to another MME. Are you sure you want to proceed with populating this MME ? Doing so will remove the existing actions associated with the fault, and this action cannot be undone.`,
                icon: 'IoMdWarning',
                title: 'Confirmation',
                buttons: [{ id: 'Yes', label: 'Yes', icon: 'IoMdSave' }, { id: 'No', label: 'No', icon: 'IoMdExit' }]
            });
            if (response === 'No') {
                return;
            }
        }
        
        for (const linkedAction of linkedActions) {
            await db.remove('FAULT_CORRECTING_ITEMS', { FAULT_CORRECTING_ITEM_ID_: linkedAction.FAULT_CORRECTING_ITEM_ID_ })
        }
        // okay now we have clear all other correcting items, lets add the new ones
        const cleanActions = await db.read('FAULT_CORRECTING_ITEMS', { FAULT_UNIT_ID_: faultUnitId });
        cleanActions.forEach((action, index) => {
            action.RECORD_NO_ = index + 1;
            action.IDX_ = index;
            db.updateRecord('FAULT_CORRECTING_ITEMS', action, ['FAULT_CORRECTING_ITEM_ID_']);
        });
        let lastRecordNo = cleanActions.length;
        for (const action of mmeActions) {
            const now = utils.dateToString(new Date());
            const id = utils.uniqueNumber();
            await db.updateRecord('FAULT_CORRECTING_ITEMS', {
                USR_ID: null,
                CAT_ID: null,
                MOD_BY_: null,
                TI_USR_ID: null,
                STS_ID: action.STATUS_ID,
                CREATED_BY_: loggedInUser.USER_ID_,
                FAULT_UNIT_ID_: correct.FAULT_UNIT_ID_,
                RECORD_NO_: lastRecordNo + 1,
                UNIT_ID_: correct.UNIT_ID_,
                IDX_: lastRecordNo,
                GROUP_NO_: utils.toNumber(rosterId),
                CREATED_ON_: now,
                FAULT_CORRECT_ID_: correct.FAULT_CORRECT_ID_,
                MNT_MAIN_HRS: null,
                TERM_: "ESNAADM",
                REL_ACT_: action.RMA_,
                MAJOR_EVENT_ID: null,
                ACT_: action.RMA_,
                MOD_ON_: null,
                FAULT_CORRECTING_ITEM_ID_: id
            }, ['FAULT_CORRECTING_ITEM_ID_']);
            lastRecordNo += 1;
        }
        db.commit();
        page.var.refreshGridQueryKey.set(utils.guid())
    }
}

