    const faultCorrect = await db.find('FAULT_CORRECTS',{FAULT_CORRECT_ID_:val.FAULT_CORRECT_ID_});
    // we are modifying the type of the correct to 132
    if(faultCorrect.ITEM_TYPE_ !== 'Correcting132'){
        // if its not Correcting132 we need to remove all the items inside first
        faultCorrect.ITEM_TYPE_ = 'Correcting132';
        await db.updateRecord('FAULT_CORRECTS',faultCorrect,'FAULT_CORRECT_ID_');
        await db.remove('FAULT_CORRECTING_ITEMS',{FAULT_CORRECT_ID_:faultCorrect.FAULT_CORRECT_ID_});
    }
    await db.updateRecord('FAULT_CORRECTING_ITEMS',val,'FAULT_CORRECTING_ITEM_ID_');
    await updateAcc132Status(val.FAULT_CORRECT_ID_);
    await db.commit();
    closePanel(true)
}

async function updateAcc132Status(faultCorrectId){
    const faultCorrect = await db.find('FAULT_CORRECTS',{FAULT_CORRECT_ID_:faultCorrectId});
    if(faultCorrect.ITEM_TYPE_ === 'Correcting132'){
        const faultUnit = await db.find('FAULT_UNITS',{FAULT_UNIT_ID_:faultCorrect.FAULT_UNIT_ID_});
        const mntStatus = await db.read('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.STS' }, { ORDER_: 'asc' });
        const correctingItems = await db.read('FAULT_CORRECTING_ITEMS',{FAULT_CORRECT_ID_:faultCorrectId});
        const accStatus = correctingItems.map(i => mntStatus.find(s => s.ID_ === i.STS_ID)).sort((a,b) => b.ORDER_ - a.ORDER_)
        if(accStatus && accStatus.length > 0){
            faultUnit.ACC_132_STS_ID = accStatus[0].ID_;
            db.updateRecord('FAULT_UNITS',faultUnit,['FAULT_UNIT_ID_'])
        }
    }
}

