    }
    
    return `<div style="display:flex;flex-direction:column;align-items:center;justify-content:center;flex-grow:1">
        ${message.join('')}
    </div>`;
}"
  },
  "DueDate": {
    "hidden": true
  },
  "DueLandings": {
    "hidden": true
  },
  "DueLandingCycle": {
    "hidden": true
  },
  "DueEquipmentCycle": {
    "hidden": true
  },
  "DueRounds": {
    "hidden": true
  },
  "CurrCycle": {
    "hidden": false,
    "title": "Remaining",
    "width": 100,
    "cellValueMapper": "
function tammsToMins(value) {
    const tammsInString = (value ?? 0).toString();
    const [hours, mins] = tammsInString.split('.')
    let totalMinutes = 0;
    if (hours) {
        totalMinutes = utils.toNumber(hours) * 60;
    }
    if (mins) {
        totalMinutes = totalMinutes + (utils.toNumber(mins) * 6);
    }
    return totalMinutes;
}

function minsToTamms(value) {
    if (value) {
        const hour = Math.floor(value / 60);
        const minutes = Math.ceil((value % 60) / 6);
        return utils.toNumber(`${hour}.${minutes}`)
    }
    return value
}

const MILISECOND_IN_A_DAY = 1000 * 60 * 60 * 24;
function remainingDays(date) {
    const dt = utils.toDate(date);
    if (dt) {
        const time = dt.getTime() - new Date().getTime();
        return Math.ceil(time / MILISECOND_IN_A_DAY)
    }
    return undefined;
}
module.exports = (val) => {
    const rowData = val.rowData;
    
    const dueHour = tammsToMins(rowData.DueHours);
    const curHour = utils.toNumber(rowData.CurrHours);
    const remHour = minsToTamms(dueHour - curHour);

    const dueCycle = utils.toNumber(rowData.DueLandingCycle);
    const curCycle = utils.toNumber(rowData.CurrCycle);
    const remCycle = utils.toNumber((dueCycle - curCycle).toFixed(1));

    const dueLanding = utils.toNumber(rowData.DueLandings);
    const curLanding = utils.toNumber(rowData.CurrLandings);
    const remLanding = utils.toNumber((dueLanding - curLanding).toFixed(1));

    const calendar = remainingDays(rowData.DueDate);
    const calendarUnit = 'Days';
    const hour = utils.isEmpty(rowData.DueHours) ? undefined : remHour;
    const landing = utils.isEmpty(rowData.DueLandings) ? undefined : remLanding;
    const landingCycle = utils.isEmpty(rowData.DueLandingCycle) ? undefined : remCycle;
    
    const message = [];
    if(hour){
        message.push(`<span style="line-height:12px">${hour} H</span>`)
    }
    if(calendar){
        message.push(`<span style="line-height:12px">${calendar} ${calendarUnit}</span>`)
    }
    if(landing){
        message.push(`<span style="line-height:12px">${landing} L</span>`)
    }
    if(landingCycle){
        message.push(`<span style="line-height:12px">${landingCycle} LC</span>`);
    }
    
    return `<div style="display:flex;flex-direction:column;align-items:center;justify-content:center;flex-grow:1">
        ${message.join('')}
    </div>`;

}"
  },
  "CurrLandings": {
    "hidden": true
  },
