variables.functionCode:
const flightId = page.var.flightId.get();

async function getPlatformSnapshotConfig() {
    const { command, platform } = app.var.appContext.get();
    const result = await page.query.findSnapshotPlatformConfig({ filter: { CMD_ID_: { type: 'equal', value: command.id }, PLTF_ID_: { type: 'equal', value: platform.id } } });
    if (result.data && result.data.length > 0) {
        return result.data[0].ID_
    }
}

async function load() {
    const flight = await db.find('FLIGHTS', { MISSION_FLIGHT_ID_: flightId });
    // getting the mission
    const mission = await db.find('MISSIONS', { MISSION_ID_: flight.MISSION_ID_ });
    // getting the wings
    const wing = await db.find('WINGS', { MISSION_ID_: mission.MISSION_ID_ });

    let pilotsAndCrews = [];
    // this is getting the actual tail flight
    const tailFlight = await db.find('TAIL_FLIGHTS', { MISSION_ID_: mission.MISSION_ID_, FLIGHT_ID: flight.MISSION_FLIGHT_ID_ });
    const tfRecord = await db.find('TAIL_FLIGHT_RECORDS', { MISSION_ID_: mission.MISSION_ID_, TAIL_FLIGHT_ID: tailFlight.TAIL_FLIGHT_ID_ })
    let crewFlights = await db.read('TAIL_CREW_FLIGHTS', { MISSION_ID_: mission.MISSION_ID_, TAIL_FLIGHT_RECORD_ID: tfRecord?.MISSION_TAIL_FLIGHT_RECORD_ID_ });
    if(crewFlights.length === 0){
        const crewPlans = await db.read('MISSION_CREW_PLANS', { MISSION_ID_: mission.MISSION_ID_ });
        crewFlights = crewPlans.map(p => ({FLIGHT_CREW_ID:p.FLIGHT_CREW_ID}))
    }

    for (const crewFlight of crewFlights) {
        let cfDetails = await db.read('TAIL_CREW_FLIGHT_DETAILS', { MISSION_ID_: mission.MISSION_ID_, TAIL_CREW_FLIGHT_ID: crewFlight.MISSION_TAIL_CREW_FLIGHT_ID_ });
        if(cfDetails.length === 0){
            const plans = await db.read('MISSION_CREW_PLANS',{MISSION_ID_:mission.MISSION_ID_,FLIGHT_CREW_ID:crewFlight.FLIGHT_CREW_ID})
            cfDetails = plans.map(i => ({DUTY_SYMBOL_ID:i.DUTY_SYMBOL_ID}));
        }
        cfDetails = [...cfDetails, ...(Array.from({ length: (3 - cfDetails.length) }).map(() => ({ DUTY_SYMBOL_ID: undefined, FLIGHT_SYMBOL_ID: undefined, FLYING_MINUTES_: undefined, PLATFORM_SEAT_ID: undefined, MISSION_TAIL_CREW_FLIGHT_DETAILS_ID_: undefined })))]
        pilotsAndCrews.push({
            crewId: crewFlight.FLIGHT_CREW_ID,
            hours: cfDetails.map(detail => {
                return {
                    dsId: detail.DUTY_SYMBOL_ID,
                    fsId: detail.FLIGHT_SYMBOL_ID,
                    hr: detail.FLYING_MINUTES_,
                    sId: detail.PLATFORM_SEAT_ID,
                    id: detail.MISSION_TAIL_CREW_FLIGHT_DETAILS_ID_
                }
            })
        })
    }
    // getting the histories
    const appContext = app.var.appContext.get();
    const unit = appContext.companies.find(c => c.id === wing.COY_ID)?.id;

    const { MISS_TYPE_ID: typeId } = (await db.find('OPS_MST_MISSION_SUB_TYPES', { ID_: mission.MISS_SUB_TYPE_ID })) ?? { SYMBOL_: undefined, MISS_TYPE_ID: undefined }
    const { EXTERNAL_ID_: externalId } = (await db.find('OPS_MST_SUPPORT_SUB_UNIT', { ID_: mission.SUPPORT_SUB_UNIT_ID })) ?? { SUB_UNIT_CODE_: undefined, ID_: undefined };
    const missionConfiguration = await db.find('OPS_MST_MISSION_CONFIGURATION', { ID_: mission.PLTF_CFG_ID });

    const atd = tailFlight.ATD_ ? new Date(tailFlight.ATD_) : undefined;
    const ata = tailFlight.ATA_ ? new Date(tailFlight.ATA_) : undefined;

    const baseFrom = await page.query.findAirBases({ filter: { ID_: { value: tailFlight.ACTUAL_BASE_FROM_ID, type: 'equal' } } });
    const fromGmtDiff = baseFrom.data && baseFrom.data.length > 0 ? baseFrom.data[0].GMT_DIFF_ : 0; // gmt diff is in minutes
    const fromGmtTime = atd ? new Date(atd.getFullYear(), atd.getMonth(), atd.getDate(), atd.getHours(), atd.getMinutes() - fromGmtDiff) : undefined;

    const arriveTo = await page.query.findAirBases({ filter: { ID_: { value: tailFlight.ACTUAL_BASE_TO_ID, type: 'equal' } } });
    const arriveGmtDiff = arriveTo.data && arriveTo.data.length > 0 ? arriveTo.data[0].GMT_DIFF_ : 0; // gmt diff is in minutes
    const arriveGmtTime = ata ? new Date(ata.getFullYear(), ata.getMonth(), ata.getDate(), ata.getHours(), ata.getMinutes() - arriveGmtDiff) : undefined;

    const duration = arriveGmtTime && fromGmtTime ? arriveGmtTime.getTime() - fromGmtTime.getTime() : undefined;
    const hour = duration ? Math.floor(duration / ONE_HOUR) : undefined;
    const minutes = duration ? Math.round((duration % ONE_HOUR) / ONE_MINUTE) : undefined;
    const minutesTamms = minutes ? Math.ceil(minutes / 6) : undefined
    const durationMinutes = duration ? Math.round(duration / ONE_MINUTE) : undefined;
    const hoursMinutes = `${hour??0}:${utils.startPad(minutes??0, 2)}`;
    const hoursTamms = `${hour??0}.${minutesTamms??0}`;


    const support = !utils.isEmpty(mission.EXTERNAL_ID);
    page.var.formValue.set({
        flightId: flightId,
        unit: unit,//: z.number(),
        date: new Date(mission.DATE_),//: z.date(),
        missionType: typeId,//: z.number(),
        missionSubType: mission.MISS_SUB_TYPE_ID,//: z.number(),
        supportedUnit: externalId,//: z.number(),
        supportedSubUnit: mission.SUPPORT_SUB_UNIT_ID,//: z.number(),
        description: mission.DESCRIPTION_,//: z.string(),
        support: support,//: z.boolean(),
        departFrom: tailFlight.ACTUAL_BASE_FROM_ID ?? undefined,//: z.number(),
        arriveTo: tailFlight.ACTUAL_BASE_TO_ID ?? undefined,//: z.number(),
        atd: atd,//: z.date(),
        ata: ata,//: z.date(),
        atdGmt: fromGmtTime,//: z.date(),
        ataGmt: arriveGmtTime,//: z.date(),
        route: mission.ROUTE_,//: z.string(),

        landing: tfRecord?.LANDINGS_,//: z.number(),
        engine1: tfRecord?.ENGINE_STARTS1_,//: z.number(),
        engine2: tfRecord?.ENGINE_STARTS2_,//: z.number(),
        cycle: tfRecord?.CYCLES_,//: z.number(),
        extLoad: tfRecord?.EXTERNAL_LOAD_,//: z.number(),
        intLoad: tfRecord?.INTERNAL_LOAD_,//: z.number(),
        passengers: tfRecord?.PASSENGERS_ ?? undefined,//: z.number(),
        passengersCode: tfRecord?.VIP_CODE_ID_ ?? undefined,//: z.number(),

        arm762: tfRecord?.ROUNDS_762_ ?? undefined,//: z.number(),
        arm20: tfRecord?.ROUNDS_20_ ?? undefined,//: z.number(),
        arm30: tfRecord?.ROUNDS_30_ ?? undefined,//: z.number(),
        arm40: tfRecord?.ROUNDS_40_ ?? undefined,//: z.number(),
        armRocket: tfRecord?.ROUNDS_ROCKET_ ?? undefined,//: z.number(),
        armTow: tfRecord?.ROUNDS_TOW_ ?? undefined,//: z.number(),
        armHellfire: tfRecord?.ROUNDS_HELLFIRE_ ?? undefined,//: z.number(),

        arm127Nag: tfRecord?.ROUNDS_127_NAG_ ?? undefined,//:z.number(),
        arm20Nag: tfRecord?.ROUNDS_20_NAG_ ?? undefined,//:z.number(),
        arm39Nag: tfRecord?.ROUNDS_AM39_NAG_ ?? undefined,//:z.number(),
        armTorpedoNag: tfRecord?.ROUNDS_TORPEDO_NAG_ ?? undefined,//:z.number(),
        armAs15ttNag: tfRecord?.ROUNDS_AS15TT_NAG_ ?? undefined,//:z.number(),

        hoursTamms: hoursTamms,
        hoursMinutes: hoursMinutes,
        durationMinutes: tfRecord?.FLIGHT_MINS_,// : z.number(),

        platformSnapshotConfiguration: mission.PLTF_CFG_ID,//:z.string(),

        platformConfiguration: missionConfiguration?.ID_,//: z.string(),
        platformConfigurationDescription: missionConfiguration?.MISSION_CONFIGURATION_,//: z.string(),

        requiredFuel: mission.PLTF_CFG_REQ_FUEL_,//: z.number(),
        uom: mission.PLTF_CFG_FUEL_UOM_ID,//: z.number(),
        maintenanceRemarks: mission.PLTF_CFG_MNT_REM_,//: z.string(),
        pilotsAndCrews: pilotsAndCrews
    });
    const history = await db.find('MISSION_HISTORIES', { MISSION_ID_: mission.MISSION_ID_ })
    const isAccomplished = history?.ACTION_ === 'Accomplish Mission';
    page.var.isAccomplished.set(isAccomplished);
}

if (flightId) {
    load();
}
const ONE_SECOND = 1000;
const ONE_MINUTE = ONE_SECOND * 60;
const ONE_HOUR = ONE_MINUTE * 60;

