module.exports = async (form) => {
    const hasPilotAndCrews = form?.pilotsAndCrews?.length > 0;
    if (!hasPilotAndCrews) {
        await alertBox({ title: 'Missing Pilot and crews', message: 'At least 2 crews is required for the mission', icon: 'IoMdWarning', buttons: [{ id: 'Ok', label: 'Ok', icon: 'IoMdSave' }] })
        return;
    }
    const saveMission = await alertBox({
        message: 'Are you sure you want to record this mission ?',
        title: 'Record Mission Confirmation',
        buttons: [{ id: 'yes', icon: 'IoMdSave', label: 'Yes' }, { id: 'no', icon: 'IoMdExit', label: 'No' }], icon: 'IoMdSave'
    });

    if (saveMission === 'yes') {

        const baseValue = await loadMissionData(form.flightId);
        const flightId = form.flightId ?? utils.uniqueNumber();

        const missionId = baseValue?.mission?.MISSION_ID_ ?? utils.uniqueNumber();
        const wingId = baseValue?.wing?.MISSION_WING_ID_ ?? utils.uniqueNumber();
        const tailFlightId = baseValue?.tailFlight?.TAIL_FLIGHT_ID_ ?? utils.uniqueNumber();
        const tailCrewId = baseValue?.tailCrews?.MISSION_TAIL_CREW_ID_ ?? utils.uniqueNumber();
        const tailFlightRecordId = baseValue?.tailFlightRecord?.MISSION_TAIL_FLIGHT_RECORD_ID_ ?? utils.uniqueNumber();

        const now = new Date();
        const missionNo = baseValue?.mission?.MISSION_NO_ ?? await generateMissionNo()
        const appContext = app.var.appContext.get();
        const unitId = appContext.tail?.unitId;
        const tailId = appContext.tail?.id;
        const platformId = appContext.platform?.id;
        const callSignId = appContext.callSigns.find(i => i.companyId === form.unit)?.id;
        const currentUser = app.var.loggedInUser.get();

        // we remove the detail first



        db.updateRecord('MISSIONS', {
            "MISSION_ID_": missionId,// 1266,
            "UNIT_ID_": unitId,//  224,
            "DATE_": utils.dateToString(form.date),// "2018-06-27T08:41:53",
            "MISS_PLAN_TYPE_": baseValue?.mission?.MISS_PLAN_TYPE_ ?? "Backdated",
            "CALL_ID": callSignId,// null,
            "MISS_SUB_TYPE_ID": form.missionSubType,// 86,
            "CREATED_BY_": baseValue?.mission?.CREATED_BY_ ?? currentUser.USER_ID_,// "MONDI",
            "CREATED_ON_": baseValue?.mission?.CREATED_ON_ ?? utils.dateToString(now),// "2018-06-27T08:42:44",
            "MOD_BY_": currentUser.USER_ID_,// "MONDI",
            "MOD_ON_": utils.dateToString(now),// "2018-06-27T08:42:44",
            "TERM_": "EsnaadM",
            "SUBMT_BY_": baseValue?.mission?.SUBMT_BY_ ?? currentUser.USER_ID_,// null,
            "SUBMT_ON_": baseValue?.mission?.SUBMT_ON_ ?? utils.dateToString(now),// null,
            "ACCESS_CODES_": baseValue?.mission?.ACCESS_CODES_, /// @TODO what is the access codes value 
            "EXTERNAL_ID": form.supportedUnit, // @TODO what is external id ??
            "CANCEL_REASON": baseValue?.mission?.CANCEL_REASON, // We cant cancel in esnaadM
            "ABORT_REASON": baseValue?.mission?.ABORT_REASON, // We cant abort in esnaadM
            "DESCRIPTION_": form.description,// null,
            "SUPPORT_SUB_UNIT_ID": form.supportedSubUnit,// null,
            "CANCEL_REASON_ID": baseValue?.mission?.CANCEL_REASON_ID, // We cant cancel in esnaadM
            "ABORT_REASON_ID": baseValue?.mission?.ABORT_REASON_ID, // We cant abort in esnaadM
            "PLTF_CFG_ID": form.platformConfiguration,// null, 
            "PLTF_CFG_REQ_FUEL_": form.requiredFuel,// null,
            "PLTF_CFG_FUEL_UOM_ID": form.uom,// null,
            "PLTF_CFG_NO_OF_PASS_": form.passengers,// null,
            "PLTF_CFG_MNT_REM_": form.maintenanceRemarks, // @TODO we might need to add MNT_REMARKS here if the mission has fault !
            "MISSION_NO_": missionNo,// "MS2018060022",
            "ROUTE_": form.route,// //null,
            "CANCEL_REASON_RMKS_": baseValue?.mission?.CANCEL_REASON_RMKS_ // We dont have cancellation here in mission 
        }, 'MISSION_ID_');

        db.updateRecord('WINGS', {
            "MISSION_ID_": missionId,//1266,
            "UNIT_ID_": unitId,//224,
            "MISSION_WING_ID_": wingId,//1445,
            "COY_ID": form.unit,//32,
            "PLTF_ID": platformId,//8,
            "IDX_": baseValue?.wing?.IDX_ ?? 0
        }, 'MISSION_WING_ID_');

        db.updateRecord('FLIGHTS', {
            "MISSION_ID_": missionId,//1266,
            "UNIT_ID_": unitId,//224,
            "MISSION_FLIGHT_ID_": flightId,// 1753, /// @TODO for time beignt we just create this using the current missionID,
            "ETD_": baseValue?.flight?.ETD_ ?? utils.dateToString(form.atd),// "2018-06-27T10:00:00",
            "ETA_": baseValue?.flight?.ETA_ ?? utils.dateToString(form.ata),// "2018-06-27T11:00:00",
            "BASE_FROM_ID": baseValue?.flight?.BASE_FROM_ID ?? form.departFrom,// 12,
            "BASE_TO_ID": baseValue?.flight?.BASE_TO_ID ?? form.arriveTo,//5,
            "CREATED_BY_": baseValue?.flight?.CREATED_BY_ ?? currentUser.USER_ID_,// "MONDI",
