                    "MISSION_ID_": missionId,//11724,
                    "UNIT_ID_": unitId,//224,
                    "MISSION_TAIL_CREW_FLIGHT_DETAILS_ID_": tailCrewFlightDetailId, //2512,
                    "FLYING_MINUTES_": hours.hr,// 5,
                    "TAIL_CREW_FLIGHT_ID": tailCrewFlightId,//detail.tailCrewFlightId,//2116,
                    "DUTY_SYMBOL_ID": hours.dsId,// 13,
                    "FLIGHT_SYMBOL_ID": hours.fsId,//1,
                    "CREATED_BY_": currentUser.USER_ID_,// "GAL7535",
                    "CREATED_ON_": utils.dateToString(now),// "2019-09-15T10:43:48",
                    "MOD_BY_": currentUser.USER_ID_,//null,
                    "MOD_ON_": utils.dateToString(now),// null,
                    "TERM_": "EsnaadM",
                    "PLATFORM_SEAT_ID": hours.sId// "9190FB23-710C-4588-A0F7-15401E6BB0E4"
                })
            })
        });

        db.updateRecord('MISSION_HISTORIES', {
            "MISSION_ID_": missionId,
            "UNIT_ID_": unitId,
            "DATE_TIME_": utils.dateToString(now),
            "USER_ID_": currentUser.USER_ID_,
            "FULL_NAME_": [currentUser.F_NAME_, currentUser.M_NAME_, currentUser.L_NAME_].filter(i => i).join(' '),
            "REMARKS_": `From : ${form.departFrom} To : ${form.arriveTo}`,
            "ACTION_": 'Record Mission'
        }, ['MISSION_ID_']);

        db.commit();
        navigate('ops/mission/recorded-mission-detail', { missionNo: missionNo });
    }
}

async function generateMissionNo() {
    const now = new Date();
    const sequence = (await db.read('MISSIONS', {})).length;
    const missionNo = `MS${now.getFullYear()}${utils.startPad(now.getMonth() + 1, 2)}${utils.startPad(now.getDate(), 2)}${utils.startPad(sequence + 1, 3)}`;
    return missionNo;
}


async function loadMissionData(flightId) {
    const flight = await db.find('FLIGHTS', { MISSION_FLIGHT_ID_: flightId });
    const mission = await db.find('MISSIONS', { MISSION_ID_: flight?.MISSION_ID_ });
    const tailCrews = await db.find('TAIL_CREWS', { MISSION_ID_: flight?.MISSION_ID_ });
    let pilotsAndCrews = [];
    const tailFlight = await db.find('TAIL_FLIGHTS', { MISSION_ID_: mission?.MISSION_ID_, FLIGHT_ID: flight?.MISSION_FLIGHT_ID_ });
    const tailFlightRecord = await db.find('TAIL_FLIGHT_RECORDS', { MISSION_ID_: mission?.MISSION_ID_, TAIL_FLIGHT_ID: tailFlight?.TAIL_FLIGHT_ID_ })
    const tailCrewFlightRecord = await db.read('TAIL_CREW_FLIGHTS', { MISSION_ID_: mission?.MISSION_ID_, TAIL_FLIGHT_RECORD_ID: tailFlightRecord?.MISSION_TAIL_FLIGHT_RECORD_ID_ });
    for (const crewFlight of tailCrewFlightRecord) {
        const tailCrewFlightRecordDetails = await db.read('TAIL_CREW_FLIGHT_DETAILS', { MISSION_ID_: mission?.MISSION_ID_, TAIL_CREW_FLIGHT_ID: crewFlight?.MISSION_TAIL_CREW_FLIGHT_ID_ });
        pilotsAndCrews.push({
            crewId: crewFlight.FLIGHT_CREW_ID,
            hours: tailCrewFlightRecordDetails.map(detail => {
                return {
                    dsId: detail.DUTY_SYMBOL_ID,
                    fsId: detail.FLIGHT_SYMBOL_ID,
                    hr: detail.FLYING_MINUTES_,
                    sId: detail.PLATFORM_SEAT_ID,
                    id: detail.MISSION_TAIL_CREW_FLIGHT_DETAILS_ID_
                }
            })
        })
    }
    // getting the wings
    const wing = await db.find('WINGS', { MISSION_ID_: mission?.MISSION_ID_ });
    const appContext = app.var.appContext.get();
    const unit = appContext.companies.find(c => c.id === wing?.COY_ID)?.id;
    return { flight, mission, tailFlight, tailFlightRecord, pilotsAndCrews, unit, wing, tailCrews }
}

