    const crewIds = value.map(c => c.crewId);
    const crews = [];
    for(const crewId of crewIds){
        const crew = await db.find('OPS_MST_FLIGHT_CREWS',{ID_:crewId});
        const person = await db.find('ADM_TRN_USER_PROFILES',{ID_:crew.PERSON_ID});
        crews.push(person);
    }
    return utils.arrayToQueryResult(crews,params,['USER_ID_','F_NAME_','M_NAME_','L_NAME_','RANK_ID'])
}

