variables.functionCode:
const missionNo = page.var.missionNo.get();

async function loadMission() {
    if (missionNo === undefined) {
        return;
    }
    // getting the mission
    const missions = await db.read('MISSIONS', { MISSION_NO_: missionNo });
    if (missions.length === 0) {
        return;
    }
    const mission = missions[0];

    // getting the mission flights
    const flights = await db.read('FLIGHTS', { MISSION_ID_: mission.MISSION_ID_ });

    // getting the wings
    const wing = await db.find('WINGS', { MISSION_ID_: mission.MISSION_ID_ });

    // getting tailCrew
    const tailCrew = await db.find('TAIL_CREWS', { MISSION_ID_: mission.MISSION_ID_ });

    // getting crew plans
    const crewPlans = await db.read('MISSION_CREW_PLANS', { MISSION_ID_: mission.MISSION_ID_ });

    // getting tail flights
    let tailFlights = [];
    let tailFlightRecords = [];
    let tailCrewFlights = [];
    let tailCrewFlightDetails = [];

    let flightsVo = [];
    let crewsVo = [];
    for (const crew of crewPlans) {

        const flightCrew = await db.find('OPS_MST_FLIGHT_CREWS', { ID_: crew.FLIGHT_CREW_ID });
        if (utils.isEmpty(flightCrew)) {
            continue;
        }
        const profile = await db.find('ADM_TRN_USER_PROFILES', { ID_: flightCrew.PERSON_ID });
        const rank = await db.find('ADM_MST_SIMPLE_REF', { ID_: profile.RANK_ID });
        const seat = await db.find('ADM_MST_PFC_SNAPSHOT_SEAT_CONFIG', { ID_: crew.SEAT_CONFIGURATION_ID_ });
        const duty = await db.find('OPS_MST_DUTY_SYMBOL', { ID_: crew.DUTY_SYMBOL_ID });

        crewsVo.push({
            userId: profile.USER_ID_,
            rank: rank?.CODE_,
            fullName: [profile?.F_NAME_, profile?.M_NAME_, profile?.L_NAME_].filter(i => i).join(' '),
            briefName: flightCrew?.BRF_NAME_,
            callSign: flightCrew?.CALL_SIGN_,
            seat: seat?.CODE_,
            duty: duty?.CODE_,
            mobile: utils.toString(profile?.MOBILE_) ?? ''
        })
    }

    for (const flight of flights) {
        const tailFlight = await db.find('TAIL_FLIGHTS', { MISSION_ID_: mission.MISSION_ID_, FLIGHT_ID: flight.MISSION_FLIGHT_ID_ });
        const { CODE_: departure } = (await db.find('ADM_MST_AIR_BASES', { ID_: flight.BASE_FROM_ID })) ?? { CODE_: undefined };
        const { CODE_: arrival } = (await db.find('ADM_MST_AIR_BASES', { ID_: flight.BASE_TO_ID })) ?? { CODE_: undefined };
        flightsVo.push({
            departure,
            arrival,
            etd: utils.hhMm(flight.ETD_),
            eta: utils.hhMm(flight.ETA_),
            status: 'Not Recorded',
            flightId: utils.toString(flight.MISSION_FLIGHT_ID_),
            atcStatus: tailFlight?.FLIGHT_STATUS_ ?? 'ReadyForMission'
        })
        if (tailFlight === undefined) {
            continue;
        }
        tailFlights.push(tailFlight);
        const tfRecord = await db.find('TAIL_FLIGHT_RECORDS', { MISSION_ID_: mission.MISSION_ID_, TAIL_FLIGHT_ID: tailFlight.TAIL_FLIGHT_ID_ })
        if (tfRecord === undefined) {
            continue;
        }
        flightsVo.find(i => i.flightId === utils.toString(flight.MISSION_FLIGHT_ID_)).status = 'Recorded';
        tailFlightRecords.push(tfRecord);
        const crewFlights = await db.read('TAIL_CREW_FLIGHTS', { MISSION_ID_: mission.MISSION_ID_, TAIL_FLIGHT_RECORD_ID: tfRecord.MISSION_TAIL_FLIGHT_RECORD_ID_ });
        tailCrewFlights = tailCrewFlights.concat(crewFlights);
        for (const crewFlight of crewFlights) {
            const cfDetails = await db.read('TAIL_CREW_FLIGHT_DETAILS', { MISSION_ID_: mission.MISSION_ID_, TAIL_CREW_FLIGHT_ID: crewFlight.MISSION_TAIL_CREW_FLIGHT_ID_ });
            tailCrewFlightDetails = tailCrewFlightDetails.concat(cfDetails);
        }
    }

    const allFlightsRecorded = flightsVo.filter(v => v.status === 'Recorded').length === flightsVo.length && flightsVo.length > 0;
    // getting the histories
    const histories = await db.read('MISSION_HISTORIES', { MISSION_ID_: mission.MISSION_ID_ });
    const appContext = app.var.appContext.get();
    const status = histories[histories.length - 1]?.ACTION_;
    const category = mission.MISS_PLAN_TYPE_;
    const platform = appContext.platform.code;
    const date = utils.ddMmmYyyy(mission.DATE_);
    const unit = appContext.companies.find(c => c.id === wing.COY_ID)?.code;
    const tail = appContext.tail.name;

    const { DESC_: subType, MISS_TYPE_ID: typeId } = (await db.find('OPS_MST_MISSION_SUB_TYPES', { ID_: mission.MISS_SUB_TYPE_ID })) ?? { SYMBOL_: undefined, MISS_TYPE_ID: undefined }
    const { DESC_: type } = (await db.find('OPS_MST_MISSION_TYPES', { ID_: typeId })) ?? { SYMBOL_: undefined };
    const { SUB_UNIT_NAME_: supportedSubUnit, EXTERNAL_ID_: externalId } = (await db.find('OPS_MST_SUPPORT_SUB_UNIT', { ID_: mission.SUPPORT_SUB_UNIT_ID })) ?? { SUB_UNIT_CODE_: undefined, ID_: undefined };
    const { NAME_: supportedUnit } = (await db.find('OPS_MST_EXTERNAL', { ID_: externalId })) ?? { NAME_: undefined };
    const description = mission.DESCRIPTION_;
    const route = mission.ROUTE_;

    const { DESCRIPTION_: configDescription, MISSION_CONFIGURATION_: config } = (await db.find('OPS_MST_MISSION_CONFIGURATION', { ID_: mission.PLTF_CFG_ID })) ?? { DESCRIPTION_: undefined, MISSION_CONFIGURATION_: undefined };
    const { CODE_: fuelUomCode } = (await db.find('ADM_MST_SIMPLE_REF', { ID_: mission.PLTF_CFG_FUEL_UOM_ID })) ?? { CODE_: undefined }
    const fuel = [(mission?.PLTF_CFG_REQ_FUEL_ ?? 0), fuelUomCode].filter(i => i).join(' ');
    const passengers = mission.PLTF_CFG_NO_OF_PASS_ ? mission.PLTF_CFG_NO_OF_PASS_.toFixed(0) : '';
    const remarks = mission.PLTF_CFG_MNT_REM_;
    page.var.allFlightsRecorded.set(allFlightsRecorded);
    page.var.formValue.set({
        missionNo,
        category,
        status,
        platform,
        date,
        unit,
        tail,
        type,
        subType,
        description,
        route,
        supportedUnit,
        supportedSubUnit,
        config,
        configDescription,
        fuel,
        passengers,
        remarks,
        flights: flightsVo,
        crews: crewsVo
    });

}

async function isAccomplished() {
    const mission = await db.find('MISSIONS', { MISSION_NO_: missionNo });
    const history = await db.find('MISSION_HISTORIES', { MISSION_ID_: mission.MISSION_ID_ });
    page.var.isAccomplished.set(history.ACTION_ === 'Accomplish Mission');
}

loadMission();
isAccomplished();

