module.exports = async () => {
    const result = await alertBox({message : 'Are you sure you want to accomplish this mission',title : 'Accomplish Mission',buttons : [{id:'Yes',label:'Yes',icon:'IoMdSave'},{id:'No',label:'No',icon:'IoMdExit'}]})
    if(result === 'No'){
        return;
    }
    const loggedInUser = app.var.loggedInUser.get()
    const missionNo = page.var.missionNo.get();
    const mission = await db.find('MISSIONS',{MISSION_NO_:missionNo});
    const now = new Date();
    db.updateRecord('MISSION_HISTORIES',{
        ACTION_ : 'Accomplish Mission',
        DATE_TIME_ : now.toISOString(),
        FULL_NAME_ : [loggedInUser.F_NAME_,loggedInUser.M_NAME_,loggedInUser.L_NAME_].filter(i => i).join(' '),
        MISSION_ID_ : mission.MISSION_ID_,
        REMARKS_: 'Accomplish Mission',
        UNIT_ID_ : mission.UNIT_ID_,
        USER_ID_ : loggedInUser.USER_ID_
    },['MISSION_ID_']);
    db.commit();
    app.var.lastRecordedMissionNo.set(missionNo);
}

