module.exports = async (params) => {

    const { userId, password } = params;
    let result = await db.find('ADM_TRN_USER_HASH', { ID_: userId });
    if (result === undefined) {
        return { message: 'Invalid user ID', success: false };
    }
    const { HASH_, SALT_ } = result;
    const hash = generateHash({ value: password, salt: SALT_ });
    if (hash === HASH_) {
        return { message: '', success: true };
    } else {
        return { message: 'Invalid Password', success: false };
    }
}

const generateHash = ({ value, salt }) => {
    const CryptoJS = "CryptoJS" in window ? window.CryptoJS : undefined;
    if (!CryptoJS) {
        return undefined;
    }
    const saltByteArray = CryptoJS.enc.Hex.parse(salt);
    const key = saltByteArray.clone();
    const iv = saltByteArray.clone();
    const sigBytes = saltByteArray.sigBytes / 2;
    key.words = saltByteArray.words.slice(0, 4);
    iv.words = saltByteArray.words.slice(4, 8);
    key.sigBytes = sigBytes;
    iv.sigBytes = sigBytes;
    const encrypted = CryptoJS.AES.encrypt(value, key, {
        mode: CryptoJS.mode.CBC,
        iv: iv,
        padding: CryptoJS.pad.Pkcs7
    });
    const md5 = CryptoJS.MD5(encrypted.toString());
    return md5.toString();
}

async function updateInspectionWhenFaultClosed(param){
    debugger;
    const NOT_DUE = 'NotDue';
    const OVERDUE = 'Overdue';
    const IN_PROGRESS = 'InProgress';

    const BEFORE_EVERY_FLIGHT = 'BeforeEveryFlight';
    const BEFORE_FLIGHT = 'BeforeFlight';
    const AFTER_EVERY_FLIGHT = 'AfterEveryFlight';
    const AFTER_FLIGHT = 'AfterFlight';
    const ENABLE_OVERDUE = 'EnableOverdue';


    const mulipliers = {
        "DAYS": 1,
        "MONTHS": 30,
        "YEARS": 360
    }

    const { faultUnitId } = param;
    const fault = await db.find('FAULT_UNITS', { FAULT_UNIT_ID_: faultUnitId });
    if (!fault) {
        return;
    }
    if (utils.isEmpty(fault.INSP_ID)) {
        return;
    }
    if (fault.FAULT_STS_ !== 'Closed') {
        return;
    }
    // okay lets find this guy in the history
    const inspectionHistory = await db.find('INSPECTION_FAULT_HISTORY', { FAULT_UNIT_ID_: faultUnitId });
    if (!utils.isEmpty(inspectionHistory.CLOSED_DATE_)) {
        return;
    }
    const inspection = await db.find('ASSIGNMENTS', { ASSIGNMENT_ID_: fault.INSP_ID });
    if (!inspection) {
        return;
    }


    const master = await db.find('MNT_TRN_INSPECTION_MASTERS', { ID_: inspection.INSP_MST_ID });
    const isCalendarBased = (master.INT_DUR_UNIT_ ?? '').length > 0;
    const isHourBased = (master.INT_DUR_HOURS_ ?? 0) > 0;
    const isCycleBased = (master.INT_DUR_CYCLES_ ?? 0) > 0;
    const isLandingBased = (master.INT_DUR_LANDINGS_ ?? 0) > 0;
    const isRoundBased = (master.INT_DUR_ROUNDS_ ?? 0) > 0;
    const isEquipmentCycleBased = (master.INT_DUR_EQP_CYCLES_ ?? 0) > 0;

    const isAfterFlight = master.INT_OTHER_ === AFTER_FLIGHT;
    const isBeforeFlight = master.INT_OTHER_ === BEFORE_FLIGHT;
    const isAfterEveryFlight = master.INT_OTHER_ === AFTER_EVERY_FLIGHT;
    const isBeforeEveryFlight = master.INT_OTHER_ === BEFORE_EVERY_FLIGHT;

    const faultCorrect = await db.find('FAULT_CORRECTS', { FAULT_UNIT_ID_: faultUnitId });
    inspectionHistory.CLOSED_DATE_ = faultCorrect.DATE_;
    inspectionHistory.CLOSED_BY_ = faultCorrect.MOD_BY_;
    inspectionHistory.CLOSED_TOT_MINUTES_ = faultCorrect.AIRCRAFT_HR_ * 60;
    inspectionHistory.CLOSED_ROUNDS_ = faultCorrect.AIRCRAFT_ROUNDS_;
    inspectionHistory.CLOSED_LANDINGS_ = faultCorrect.AIRCRAFT_LANDINGS_;
    inspectionHistory.CLOSED_CYCLES_ = faultCorrect.AIRCRAFT_CYCLES_;

    inspectionHistory.CLOSED_DUE_CYCLES_ = inspection.DUE_CYCLES_;
    inspectionHistory.CLOSED_DUE_DATE_ = inspection.DUE_DATE_;
    inspectionHistory.CLOSED_DUE_HOURS_ = inspection.DUE_HOURS_;
    inspectionHistory.CLOSED_DUE_LANDINGS_ = inspection.DUE_LANDINGS_;
    inspectionHistory.CLOSED_DUE_MAX_CYCLES_ = inspection.DUE_MAX_CYCLES_;
    inspectionHistory.CLOSED_DUE_MAX_DATE_ = inspection.DUE_MAX_DATE_;
    inspectionHistory.CLOSED_DUE_MAX_HOURS_ = inspection.DUE_MAX_HOURS_;
    inspectionHistory.CLOSED_DUE_MAX_LANDINGS_ = inspection.DUE_MAX_LANDINGS_;
    inspectionHistory.CLOSED_DUE_MAX_ROUNDS_ = inspection.DUE_MAX_ROUNDS_;
    inspectionHistory.CLOSED_DUE_MIN_CYCLES_ = inspection.DUE_MIN_CYCLES_;
    inspectionHistory.CLOSED_DUE_MIN_DATE_ = inspection.DUE_MIN_DATE_;
    inspectionHistory.CLOSED_DUE_MIN_HOURS_ = inspection.DUE_MIN_HOURS_;
    inspectionHistory.CLOSED_DUE_MIN_LANDINGS_ = inspection.DUE_MIN_LANDINGS_;
    inspectionHistory.CLOSED_DUE_MIN_ROUNDS_ = inspection.DUE_MIN_ROUNDS_;
    inspectionHistory.CLOSED_DUE_MIN_CYCLES_ = inspection.DUE_MIN_CYCLES_;
    inspectionHistory.CLOSED_DUE_ROUNDS_ = inspection.DUE_MIN_ROUNDS_;
    inspectionHistory.CLOSED_EQP_CYCLES_ = inspection.DUE_EQP_CYCLES_;


    const NotYetDue = await db.find('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.INS', CODE_: 'NYD' });
    inspection.INSP_STAT_ID = NotYetDue.ID_;

    const noTolerance = master.TOLERANCE_WINDOW_TYPE_ !== ENABLE_OVERDUE;
    const enableTolerance = !noTolerance;

    if (isHourBased) {
        let nextDueInHour = faultCorrect.AIRCRAFT_HR_ + master.INT_DUR_HOURS_;
        let dueTracehold = utils.toNumber((0.1 * master.INT_DUR_HOURS_).toFixed(1));
        dueTracehold = dueTracehold > 5 ? 5 : dueTracehold;
        if (enableTolerance && (faultCorrect.AIRCRAFT_HR_ < (inspection.DUE_HOURS_ + dueTracehold))) {
            nextDueInHour = inspection.DUE_HOURS_ + master.INT_DUR_HOURS_;
        }
        inspection.DUE_HOURS_ = nextDueInHour;
        inspection.DUE_MAX_HOURS_ = enableTolerance ? nextDueInHour + dueTracehold : nextDueInHour;
        inspection.DUE_MIN_HOURS_ = utils.toNumber((nextDueInHour - dueTracehold).toFixed(1));
    }

    if (isCycleBased) {
        let nextDueInCycle = faultCorrect.AIRCRAFT_CYCLES_ + master.INT_DUR_CYCLES_;
        const dueTracehold = 50;
        if (enableTolerance && (faultCorrect.AIRCRAFT_CYCLES_ < (inspection.DUE_CYCLES_ + dueTracehold))) {
            nextDueInCycle = inspection.DUE_CYCLES_ + master.INT_DUR_CYCLES_;
        }
        inspection.DUE_CYCLES_ = nextDueInCycle;
        inspection.DUE_MAX_CYCLES_ = enableTolerance ? nextDueInCycle + dueTracehold : nextDueInCycle;
        inspection.DUE_MIN_CYCLES_ = nextDueInCycle - dueTracehold;
    }

    if (isLandingBased) {
        let nextDueInLanding = faultCorrect.AIRCRAFT_LANDINGS_ + master.INT_DUR_LANDINGS_;
        const dueTracehold = 50;
        if (enableTolerance && (faultCorrect.AIRCRAFT_LANDINGS_ < (inspection.DUE_LANDINGS_ + dueTracehold))) {
            nextDueInLanding = inspection.DUE_LANDINGS_ + master.INT_DUR_LANDINGS_;
        }
        inspection.DUE_LANDINGS_ = nextDueInLanding;
        inspection.DUE_MAX_LANDINGS_ = enableTolerance ? nextDueInLanding + dueTracehold : nextDueInLanding;
        inspection.DUE_MIN_LANDINGS_ = nextDueInLanding - dueTracehold;
    }

    if (isCalendarBased) {
        const durationUnit = master.INT_DUR_UNIT_.toUpperCase();
        const dayFactor = utils.toNumber(mulipliers[durationUnit]);
        const durationInDays = master.INT_DUR_VALUE_ * dayFactor;

        let nextDueInCalendar = utils.dateAdd(faultCorrect.DATE_, durationInDays, 'day');
        let dueTracehold = Math.round(0.1 * durationInDays);
        dueTracehold = dueTracehold > 30 ? 30 : dueTracehold;
        if (enableTolerance && (utils.toDate(faultCorrect.DATE_) < utils.dateAdd(inspection.DUE_DATE_, dueTracehold, 'day'))) {
            nextDueInCalendar = utils.dateAdd(inspection.DUE_DATE_, durationInDays, 'day');
        }
        inspection.DUE_DATE_ = utils.dateToString(nextDueInCalendar)
        inspection.DUE_MAX_DATE_ = enableTolerance ? utils.dateToString(utils.dateAdd(nextDueInCalendar, dueTracehold, 'day')) : utils.dateToString(nextDueInCalendar);
        inspection.DUE_MIN_DATE_ = utils.dateToString(utils.dateAdd(nextDueInCalendar, -dueTracehold, 'day'));

    }
    inspection.MOD_BY_ = 'SYSTEM';
    inspection.MOD_ON_ = utils.dateToString(new Date());
    await db.updateRecord('INSPECTION_FAULT_HISTORY', inspectionHistory, 'FAULT_UNIT_ID_');
    await db.updateRecord('ASSIGNMENTS', inspection, 'ASSIGNMENT_ID_');
    await db.commit();
}

module.exports = updateInspectionWhenFaultClosed;

async function processAllInspection() {
    const loggedInUser = app.var.loggedInUser.get();
    const context = app.var.appContext.get();

    const NOT_DUE = 'NotDue';
    const OVERDUE = 'Overdue';
    const IN_PROGRESS = 'InProgress';

    const BEFORE_EVERY_FLIGHT = 'BeforeEveryFlight';
    const BEFORE_FLIGHT = 'BeforeFlight';
    const AFTER_EVERY_FLIGHT = 'AfterEveryFlight';
    const AFTER_FLIGHT = 'AfterFlight';
    const ENABLE_OVERDUE = 'EnableOverdue';

    const multipliers = {
        "DAYS": 1,
        "MONTHS": 30,
        "YEARS": 360
    }
    const Status = {
        NotYetDue: 'NYD',
        OverDue: 'OVD',
        InProgress: 'IPR'
    }

    log.debug('Process All Inspections');
    // First we fetch all assignments in the database and all masters required
    const assignments = await db.read('ASSIGNMENTS', {});
    const inspectionStatus = await db.read('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.INS' });
    const faultStatusStatus = await db.read('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.STS' }, { ORDER_: 'asc' });
    const inProgress = inspectionStatus.find(i => i.CODE_ === Status.InProgress)?.ID_;
    const notYetDue = inspectionStatus.find(i => i.CODE_ === Status.NotYetDue)?.ID_;
    const overdue = inspectionStatus.find(i => i.CODE_ === Status.OverDue)?.ID_;

    const { unitId: tailUnitId, id: tailId, name: tailNo } = context.tail;
    const { cycles, hours, landings, recordedCycles, recordedHours, recordedLandings } = context.tailStatus;
    log.debug('loggedInUser', loggedInUser)
    const totalCycles = cycles + recordedCycles;
    const totalHours = parseFloat(((hours + recordedHours) / 60).toFixed(1));
    const totalLandings = landings + recordedLandings;
    log.debug('totalCycles', totalCycles, 'totalHours', totalHours, 'totalLandings', totalLandings);

    const faultUnitCount = (await db.query('select count(*) totalFaults from FAULT_UNITS', {}))[0];
    let totalFaults = utils.toNumber(faultUnitCount.totalFaults);
    log.debug('totalFaults', totalFaults);
    const now = utils.dateToString(new Date());
    for (const assignment of assignments) {
        
        // now we are processing the assigments
        log.debug('Processing assignment', assignment);
        const fault = await db.find('FAULT_UNITS', { INSP_ID: assignment.ASSIGNMENT_ID_, FAULT_STS_: 'Open' });

        const master = await db.find('MNT_TRN_INSPECTION_MASTERS', { ID_: assignment.INSP_MST_ID });
        // here we identify the type of assignment
        const isCalendarBased = (master.INT_DUR_UNIT_ ?? '').length > 0 && ((master.INT_DUR_VALUE_ ?? 0) > 0);
        const isHourBased = (master.INT_DUR_HOURS_ ?? 0) > 0;
        const isCycleBased = (master.INT_DUR_CYCLES_ ?? 0) > 0;
        const isLandingBased = (master.INT_DUR_LANDINGS_ ?? 0) > 0;
        const isRoundBased = (master.INT_DUR_ROUNDS_ ?? 0) > 0;
        const isEquipmentCycleBased = (master.INT_DUR_EQP_CYCLES_ ?? 0) > 0;

        const isAfterFlight = master.INT_OTHER_ === AFTER_FLIGHT;
        const isBeforeFlight = master.INT_OTHER_ === BEFORE_FLIGHT;
        const isAfterEveryFlight = master.INT_OTHER_ === AFTER_EVERY_FLIGHT;
        const isBeforeEveryFlight = master.INT_OTHER_ === BEFORE_EVERY_FLIGHT;

        const noTolerance = master.TOLERANCE_WINDOW_TYPE_ !== ENABLE_OVERDUE;
        const enableTolerance = !noTolerance;
        let assignmentIsUpdated = false;
        log.debug('assignmentInfo', { isCalendarBased, isHourBased, isCycleBased, isLandingBased, isRoundBased, isEquipmentCycleBased });
        // next we determine if the assigment is required to be upgraded !
        if (isHourBased) {
            const nextDueHour = assignment.DUE_HOURS_;
            const remainingHour =  utils.toNumber((nextDueHour - totalHours).toFixed(1));

            let dueHourTracehold = utils.toNumber((0.1 * master.INT_DUR_HOURS_).toFixed(1));
            dueHourTracehold = dueHourTracehold > 5 ? 5 : dueHourTracehold;

            let remainingHoursIsLesserThanTracehold = remainingHour <= dueHourTracehold;
            let remainingHoursIsLesserThanZero = remainingHour < 0;
            let remainingHoursIsLesserThanNegativeTracehold = remainingHour <= -dueHourTracehold;

            if (remainingHoursIsLesserThanTracehold && assignment.INSP_STAT_ID === notYetDue) {
                assignment.INSP_STAT_ID = inProgress;
                assignmentIsUpdated = true;
            }
            if (remainingHoursIsLesserThanZero && noTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            if (remainingHoursIsLesserThanNegativeTracehold && enableTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
        }

        if (isCycleBased) {
            const nextDueCycle = assignment.DUE_CYCLES_;
            const remainingCycles = nextDueCycle - totalCycles;
            const dueCycleTracehold = 50;
            const remainingCyclesIsLesserThanTracehold = remainingCycles <= dueCycleTracehold;
            const remainingCyclesIsLesserThanZero = remainingCycles < 0;
            const remainingCyclesIsLesserThanNegativeTracehold = remainingCycles <= -dueCycleTracehold;

            if (remainingCyclesIsLesserThanTracehold && assignment.INSP_STAT_ID === notYetDue) {
                assignment.INSP_STAT_ID = inProgress;
                assignmentIsUpdated = true;
            }
            if (remainingCyclesIsLesserThanZero && noTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            if (remainingCyclesIsLesserThanNegativeTracehold && enableTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            /*
            const DUE_MIN_CYCLES_ = nextDueCycle - dueCycleTracehold;
            const DUE_MAX_CYCLES_ = noTolerance ? nextDueCycle : nextDueCycle + dueCycleTracehold;

            if (DUE_MIN_CYCLES_ !== assignment.DUE_MIN_CYCLES_) {
                assignment.DUE_MIN_CYCLES_ = DUE_MIN_CYCLES_;
                assignmentIsUpdated = true;
            }
            if (DUE_MAX_CYCLES_ !== assignment.DUE_MAX_CYCLES_) {
                assignment.DUE_MAX_CYCLES_ = DUE_MAX_CYCLES_;
                assignmentIsUpdated = true;
            }
            */

        }

        if (isLandingBased) {
            const nextDueLanding = assignment.DUE_LANDINGS_;

            const remainingLandings = nextDueLanding - totalLandings;
            const dueLandingTracehold = 50;

            const remainingLandingsIsLesserThanTracehold = remainingLandings <= dueLandingTracehold;
            const remainingLandingsIsLesserThanZero = remainingLandings < 0;
            const remainingLandingsIsLesserThanNegativeTracehold = remainingLandings <= -dueLandingTracehold;

            if (remainingLandingsIsLesserThanTracehold && assignment.INSP_STAT_ID === notYetDue) {
                assignment.INSP_STAT_ID = inProgress;
                assignmentIsUpdated = true;
            }
            if (remainingLandingsIsLesserThanZero && noTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            if (remainingLandingsIsLesserThanNegativeTracehold && enableTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            /*
            const DUE_MIN_LANDINGS_ = nextDueLanding - dueLandingTracehold;
            const DUE_MAX_LANDINGS_ = noTolerance ? nextDueLanding : nextDueLanding + dueLandingTracehold;

            if (DUE_MIN_LANDINGS_ !== assignment.DUE_MIN_LANDINGS_) {
                assignment.DUE_MIN_LANDINGS_ = DUE_MIN_LANDINGS_;
                assignmentIsUpdated = true;
            }
            if (DUE_MAX_LANDINGS_ !== assignment.DUE_MAX_LANDINGS_) {
                assignment.DUE_MAX_LANDINGS_ = DUE_MAX_LANDINGS_;
                assignmentIsUpdated = true;
            }
            */
        }

        if (isCalendarBased) {
            const remainingDays = diffToDays(new Date(), assignment.DUE_DATE_);
            const durationUnit = master.INT_DUR_UNIT_.toUpperCase();
            let dueDateTraceHold = utils.toNumber(((0.1 * master.INT_DUR_VALUE_) * multipliers[durationUnit]).toFixed(1));
            dueDateTraceHold = dueDateTraceHold > 30 ? 30 : dueDateTraceHold;
            var remainingDaysIsLesserThanTracehold = remainingDays <= dueDateTraceHold;
            var remainingDaysIsLesserThanZero = remainingDays < 0;
            var remainingDaysIsLesserThanNegativeTracehold = remainingDays < -dueDateTraceHold;

            if (remainingDaysIsLesserThanTracehold && assignment.INSP_STAT_ID === notYetDue) {
                assignment.INSP_STAT_ID = inProgress;
                assignmentIsUpdated = true;
            }
            if (remainingDaysIsLesserThanZero && noTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            if (remainingDaysIsLesserThanNegativeTracehold && enableTolerance && assignment.INSP_STAT_ID !== overdue) {
                assignment.INSP_STAT_ID = overdue;
                assignmentIsUpdated = true;
            }
            /*
            const DUE_MIN_DATE_ = utils.toString(utils.dateAdd(assignment.DUE_DATE_, -dueDateTraceHold, 'day'));
            const DUE_MAX_DATE_ = noTolerance ? assignment.DUE_MAX_DATE_ : utils.toString(utils.dateAdd(assignment.DUE_DATE_, dueDateTraceHold, 'day'));

            if (DUE_MIN_DATE_ !== assignment.DUE_MIN_DATE_) {
                assignment.DUE_MIN_DATE_ = DUE_MIN_DATE_;
                assignmentIsUpdated = true;
            }
            if (DUE_MAX_DATE_ !== assignment.DUE_MAX_DATE_) {
                assignment.DUE_MAX_DATE_ = DUE_MAX_DATE_;
                assignmentIsUpdated = true;
            }
            */
        }

        if (isAfterFlight || isBeforeFlight || isAfterEveryFlight || isBeforeEveryFlight) {
            const lastFaultStatusIsClosed = fault === undefined;

            if (lastFaultStatusIsClosed && assignment.INSP_STAT_ID !== inProgress) {
                assignment.INSP_STAT_ID = inProgress;
                assignmentIsUpdated = true;
            }

            if (!lastFaultStatusIsClosed && (isBeforeFlight || isBeforeEveryFlight)) {
                const lastFaultOpenHoursLesserThanCurrentAircraftHours = fault.FAULT_INFO_AIRCRAFT_HR_ < totalHours;
                if (lastFaultOpenHoursLesserThanCurrentAircraftHours) {
                    assignment.INSP_STAT_ID = overdue;
                    assignmentIsUpdated = true;
                }
            }
        }
        debugger;
        if (assignmentIsUpdated) {
            assignment.MOD_BY_ = 'ESNAADM'
            assignment.MOD_ON_ = now;
            await db.updateRecord('ASSIGNMENTS', assignment, 'ASSIGNMENT_ID_');
            log.debug('assignmentIsUpdated', assignment);
        } else {
            log.debug('No change in assignment status', assignment);
        }

        // Now here we need to check if we need to create fault for the inspection
        const inspectionOverdueOrInProgressButNoFault = [inProgress, overdue].indexOf(assignment.INSP_STAT_ID) >= 0 && fault === undefined;
        const inspectionOverdueOrInProgressAndHasFault = [inProgress, overdue].indexOf(assignment.INSP_STAT_ID) >= 0 && fault !== undefined;

        if (inspectionOverdueOrInProgressButNoFault) {
            // here we are creating the fault
            const fourDigitYear = utils.startPad(new Date().getFullYear(), 4);
            const fourDigitTail = utils.startPad(tailNo, 4);
            const threeDigitTotalFault = utils.startPad(totalFaults++, 3);
            const faultNo = `AF${fourDigitYear}${fourDigitTail}${threeDigitTotalFault}`;
            const nextDueString = isCalendarBased ? " DUE AT " + utils.ddMmmYyyy(assignment.DUE_DATE_) : "";
            const inspectionNumber = master.INSP_NUMBER_;
            const inspectionReference = master.INSP_REF_;
            const inspectionRemarks = master.REMARKS_;
            const inspectionName = master.INSP_NAME_;
            const text = [];
            text.push(inspectionName + " (" + inspectionNumber + ") " + nextDueString);
            text.push("REF : " + (!utils.isEmpty(inspectionReference) ? inspectionReference : 'N/A'));
            text.push("REMARKS : " + (!utils.isEmpty(inspectionRemarks) ? inspectionRemarks : 'N/A'));
            text.push("ESNAADM FAULT NO : " + faultNo + " CREATED ON : " + utils.ddMmmYyyy(new Date()));

            const faultUnitId = utils.uniqueNumber();

            log.debug('recordingFault', faultUnitId);
            const FAULT_UNIT = {
                ACC_132_STS_ID: null,
                ACC_STS_ID: master.FM_STAT_ID,
                BACKDATED_: 0,
