                FAULT_INFO_HAS_MOC: master.HAS_MOC,
                FAULT_INFO_HAS_MTF: master.HAS_MTF,
                FAULT_INFO_MEFF_ID: master.FM_MALF_ID,
                FAULT_INFO_NLT_ACFT_HRS_: null,
