                FAULT_INFO_ROUNDS_: null,
                FAULT_INFO_STS_ID: master.FM_STAT_ID,
                FAULT_INFO_SYS_ID: master.FM_SYS_ID,
                FAULT_INFO_USR_ID: loggedInUser.ID_,
                FAULT_NO_: faultNo,
                FAULT_STATE_: 'FaultInfoSaved',
