                FAULT_UNCORRECTED_DT_141_: null,
                FAULT_UNCORRECTED_DT_BEGIN_: null,
                FAULT_UNCORRECTED_DT_END: null,
                FAULT_UNCORRECTED_INSP_ID_: null,
                FAULT_UNCORRECTED_REMARKS_: "",
                FAULT_UNCORRECTED_REQ_: null,
                FAULT_UNCORRECTED_REQ_ID_: null,
                FAULT_UNCORRECTED_USR_ID: null,
                FAULT_UNCORRECTED_WO_ID_: null,
                FAULT_UNIT_ID_: faultUnitId,
                FULL_NAME_CREATED_BY_: 'ESNAADM',
                FULL_NAME_MOD_BY_: null,
                INSP_ID: assignment.ASSIGNMENT_ID_,
                IS_GENERATED_FROM_4_1_: 0,
                IS_NEW_: null,
                MOD_BY_: null,
                MOD_ON_: null,
                OLD_TAIL_FLIGHT_ID_: null,
                PID_CREATED_BY_: null,
                PID_MOD_BY_: null,
                PRNT_FAULT_UNIT_ID: null,
                TAIL_FLIGHT_ID: null,
                TERM_: null,
                UNIT_ID: tailUnitId
            };


            await db.record('FAULT_ACTIVITIES', {
                ACTION_: 'Save Fault Information',
                DATE_: now,
                FAULT_ACTIVITY_ID_: utils.guid(),
                FAULT_UNIT_ID_: faultUnitId,
                FIELD_: null,
                NEW_: null,
                OLD_: null,
                PID_: 'ESNAADM',
                RECORD_: 'N/A',
                UNIT_ID_: tailUnitId,
                USER_ID_: 'ESNAADM'
            })

            const hasMme = !utils.isEmpty(master.MME_ID);
            if (hasMme) {
                FAULT_UNIT.FAULT_STATE_ = 'Correcting';

                const faultCorrectId = utils.uniqueNumber();
                log.debug('Recording fault correct ', faultCorrectId);
                const FAULT_CORRECTS = {
                    ACT_DESC_: null,
                    ACT_ID: null,
                    AIRCRAFT_CYCLES_: null,
                    AIRCRAFT_HR_: null,
                    AIRCRAFT_LANDINGS_: null,
                    AIRCRAFT_ROUNDS_: null,
                    CREATED_BY_: 'ESNAADM',
                    CREATED_ON_: now,
                    DATE_: null,
                    FAULT_CORRECT_ID_: faultCorrectId,
                    FAULT_UNIT_ID_: faultUnitId,
                    FG_ID: null,
                    ITEM_TYPE_: 'Correcting132',
                    LAST_RECORD_NO_: 0,
                    MOD_BY_: null,
