                    FAULT_ACTIVITY_ID_: utils.guid(),
                    FAULT_UNIT_ID_: faultUnitId,
                    FIELD_: 'N/A',
                    NEW_: `MME : ${mme.DESC_}`,
                    OLD_: 'N/A',
                    PID_: 'ESNAADM',
                    RECORD_: 'N/A',
                    UNIT_ID_: tailUnitId,
                    USER_ID_: 'ESNAADM'
                });
                FAULT_UNIT.ACC_132_STS_ID = sortedActionsBasedOnStatus.length > 0 ? sortedActionsBasedOnStatus[0].STATUS_ID : undefined;
                FAULT_CORRECTS.LAST_RECORD_NO_ = recordNo;
