                CLOSED_DUE_MIN_CYCLES_: null,
                CLOSED_DUE_DATE_: null,
                CLOSED_DUE_HOURS_: null,
                CLOSED_DUE_LANDINGS_: null,
                CLOSED_DUE_CYCLES_: null,
                CLOSED_DUE_MAX_DATE_: null,
                CLOSED_DUE_MAX_HOURS_: null,
                CLOSED_DUE_MAX_LANDINGS_: null,
                CLOSED_DUE_MAX_CYCLES_: null,
                CLOSED_BY_: null,
                CLOSED_ROUNDS_: null,// 0,
