                CLOSED_DUE_ROUNDS_: null,
                CLOSED_DUE_MAX_ROUNDS_: null,
                CLOSED_EQP_CYCLES_: null,
            })
        }

        if (inspectionOverdueOrInProgressAndHasFault) {
            if (fault.FAULT_INFO_STS_ID !== master.FM_OD_STAT_ID) {
                const prevStatusId = fault.FAULT_INFO_STS_ID;
                fault.FAULT_INFO_STS_ID = master.FM_OD_STAT_ID;
                await db.record('FAULT_ACTIVITIES', {
                    ACTION_: 'Upgrade Status for exceeding the tolerance window',
