                    FAULT_ACTIVITY_ID_: utils.guid(),
                    FAULT_UNIT_ID_: fault.FAULT_UNIT_ID_,
                    FIELD_: 'Status',
                    NEW_: faultStatusStatus.find(i => i.ID_ === fault.FAULT_INFO_STS_ID)?.CODE_,
                    OLD_: faultStatusStatus.find(i => i.ID_ === prevStatusId)?.CODE_,
                    PID_: 'ESNAADM',
                    RECORD_: 'N/A',
                    UNIT_ID_: tailUnitId,
                    USER_ID_: 'ESNAADM'
                });
                await db.updateRecord('FAULT_UNITS', fault, 'FAULT_UNIT_ID_');
            }
        }
    }
    db.commit();
    log.debug("done");
}

async function updateAdhocFaultNolaterThanRule() {
    const { cycles, hours, landings, recordedCycles, recordedHours, recordedLandings } = app.var.appContext.get().tailStatus;
    const totalHours = parseFloat(((hours + recordedHours) / 60).toFixed(1));
    const totalLandings = landings + recordedLandings;
    const totalCycles = cycles + recordedCycles;
    const faultStatuses = await db.read('ADM_MST_SIMPLE_REF', { CODE_: 'MNT.STS' });
    const faults = await db.read('FAULT_UNITS', { FAULT_STS_: 'Open', BACKDATED_: 1 });
    for (const fault of faults) {
        const hasNLT = !utils.isEmpty(fault.FAULT_INFO_NLT_STATUS_UPGRD_ID);
        if (!hasNLT) {
            continue;
        }
        const faultIsUpgraded = fault.FAULT_INFO_STS_ID === fault.FAULT_INFO_NLT_STATUS_UPGRD_ID;
        if (faultIsUpgraded) {
            continue;
        }
        const reasonStatusUpgraded = [];
        const dateToUpgrade = utils.isEmpty(fault.FAULT_INFO_NLT_DATE_) ? undefined : utils.toDate(fault.FAULT_INFO_NLT_DATE_);
        const upgradeBecauseOfDate = dateToUpgrade && (dateToUpgrade < new Date());
        const upgradeBecauseOfHours = fault.FAULT_INFO_AIRCRAFT_HR_ < totalHours;
        const upgradeBecauseOfLandings = fault.FAULT_INFO_NLT_CYCLES_ < totalLandings;
        const upgradeBecauseOfCycles = fault.FAULT_INFO_NLT_CYCLES_ < totalCycles;
        const needToBeUpgraded = upgradeBecauseOfDate || upgradeBecauseOfHours || upgradeBecauseOfLandings || upgradeBecauseOfCycles;
        if (!needToBeUpgraded) {
            continue;
        }
        if (upgradeBecauseOfDate) {
            reasonStatusUpgraded.push('date');
        }
        if (upgradeBecauseOfHours) {
            reasonStatusUpgraded.push('hours');
        }
        if (upgradeBecauseOfLandings) {
            reasonStatusUpgraded.push('landings');
        }
        if (upgradeBecauseOfCycles) {
            reasonStatusUpgraded.push('cycles');
        }

        fault.FAULT_INFO_NLT_STATUS_BFR_UPGRD_ID = fault.FAULT_INFO_STS_ID;
        fault.FAULT_INFO_STS_ID = fault.FAULT_INFO_NLT_STATUS_UPGRD_ID;
        await db.updateRecord('FAULT_UNITS', fault, 'FAULT_UNIT_ID_');
        await db.record('FAULT_ACTIVITIES', {
            FAULT_ACTIVITY_ID_: utils.guid(),
            FAULT_UNIT_ID_: fault.FAULT_UNIT_ID_,
            ACTION_: 'Upgrade Status because overdue ' + reasonStatusUpgraded.join(', '),
            DATE_: utils.toString(new Date()),
            FIELD_: 'Status',
            OLD_: faultStatuses.find(i => i.ID_ === fault.FAULT_INFO_STS_ID).CODE_,
            NEW_: faultStatuses.find(i => i.ID_ === fault.FAULT_INFO_NLT_STATUS_UPGRD_ID).CODE_,
            PID_: 'ESNAADM',
            RECORD_: 'N/A'
        })
        
    }
}

const ONE_DAY_TIME = 1000 * 60 * 60 * 24;

function diffToDays(dateFrom, dateTo) {
    dateFrom = utils.toDate(dateFrom)
    let tmpDateTo = utils.toDate(dateTo);

    if (dateFrom && tmpDateTo) {
        const dateTo = new Date(tmpDateTo.getFullYear(), tmpDateTo.getMonth(), tmpDateTo.getDate(), 24, 59, 59, 0);
        const dateDiffTime = dateTo.getTime() - dateFrom.getTime();
        let difference = dateDiffTime / ONE_DAY_TIME;
        difference = Math.floor(difference);
        return difference;
    }
    return NaN;
}

module.exports = processAllInspection

