const user = utils.getItem('app-user');
debugger;
module.exports = user;

const lastRecordedFaultNoChange = app.var.lastRecordedFaultNo.get();

const currentContext = app.var.appContext.get();
const appContext = structuredClone(currentContext);
(async () => {
    if (appContext?.tailStatus) {
        const statusses = await db.read('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.STS' });
        const aircraftSys = await db.find('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.MSY',CODE_ :'A' });
        const armamentSys = await db.find('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.MSY',CODE_ :'W' });
        const electronicSys = await db.find('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.MSY',CODE_ :'E' });
        const otherSys = await db.find('ADM_MST_SIMPLE_REF', { GRP_: 'MNT.MSY',CODE_ :'O' });

        const openFaults = await db.read('FAULT_UNITS', { FAULT_STS_: 'Open' });
        // first we clear all the status
        appContext.tailStatus.statusAircraftId = undefined;
        appContext.tailStatus.statusArmamentId = undefined;
        appContext.tailStatus.statusElectronicId = undefined;
        appContext.tailStatus.statusOtherId = undefined;
        for (const fault of openFaults) {
            const isAircraftFault = fault.FAULT_INFO_SYS_ID === aircraftSys.ID_;
            const isArmamentFault = fault.FAULT_INFO_SYS_ID === armamentSys.ID_;
            const isElectronicFault = fault.FAULT_INFO_SYS_ID === electronicSys.ID_;
            const isOtherFault = fault.FAULT_INFO_SYS_ID === otherSys.ID_;
            const currentAircraftStatus = statusses.find(i => {
                if(isAircraftFault){
                    return i === appContext.tailStatus.statusAircraftId
                }
                if(isArmamentFault){
                    return i === appContext.tailStatus.statusArmamentId
                }
                if(isElectronicFault){
                    return i === appContext.tailStatus.statusElectronicId
                }
                if(isOtherFault){
                    return i === appContext.tailStatus.statusOtherId
                }
                return undefined;
            });

            const nextAircraftStatus = statusses.find(i => i.ID_ === fault.ACC_STS_ID);
            
            if(nextAircraftStatus?.ORDER_ && nextAircraftStatus?.ORDER_ < currentAircraftStatus?.ORDER_){
                continue;
            }

            if(isAircraftFault){
                appContext.tailStatus.statusAircraftId = nextAircraftStatus.ID_
            }
            if(isArmamentFault){
                appContext.tailStatus.statusArmamentId = nextAircraftStatus.ID_
            }
            if(isElectronicFault){
                appContext.tailStatus.statusElectronicId = nextAircraftStatus.ID_
            }
            if(isOtherFault){
                appContext.tailStatus.statusOtherId = nextAircraftStatus.ID_
            }
        }
        if(JSON.stringify(currentContext) !== JSON.stringify(appContext)){
            app.var.appContext.set(appContext);
        }
    }
})()

const missionNo = app.var.lastRecordedMissionNo.get();
(async () => {
    const data =  await db.query(`select tfr.FLIGHT_MINS_,tfr.LANDINGS_,tfr.CYCLES_ from MISSIONS m
    left join FLIGHTS f on f.MISSION_ID_ = m.MISSION_ID_
    left join TAIL_FLIGHTS tf on tf.FLIGHT_ID = f.MISSION_FLIGHT_ID_
    left join TAIL_FLIGHT_RECORDS tfr on tfr.TAIL_FLIGHT_ID = tf.TAIL_FLIGHT_ID_ where tfr.FLIGHT_MINS_ is not null`,{});
    let totalMinutes = 0;
    let totalCycles = 0;
    let totalLandings = 0;
    for(const d of data){
        totalMinutes += (utils.toNumber(d.FLIGHT_MINS_) ?? 0);
        totalCycles += (utils.toNumber(d.CYCLES_) ?? 0);
        totalLandings += (utils.toNumber(d.LANDINGS_) ?? 0);
    }
    const context = {...app.var.appContext.get()};
    context.tailStatus = {...context.tailStatus};
    context.tailStatus.recordedCycles = totalCycles;
    context.tailStatus.recordedHours = totalMinutes;
    context.tailStatus.recordedLandings = totalLandings;
    app.var.appContext.set(context);
})()

const tailNumber = app.var.tailNumber.get();
log.setLevel('info');
(async () => {
    if (!tailNumber) {
        log.info('There is no tailNumber',tailNumber,'exiting populateAppContext');
        return;
    }
    const battalionAndCommandResponse = await app.query.getTailBattalionAndCommandFromTailNumber({ params: { tailNumber: tailNumber } });
    log.debug('battalionAndCommandResponse',battalionAndCommandResponse);
    const currentContext = { ...app.var.appContext.get() };
    let battalionId = 0;

    if (battalionAndCommandResponse.data && battalionAndCommandResponse.data.length > 0) {
        const item = battalionAndCommandResponse.data[0];
        log.debug('battalionCommandItem',item);
        currentContext.command = { id: item.COMMAND_ID_, code: item.COMMAND_CODE_, name: item.COMMAND_NAME_ }
        currentContext.battalion = { id: item.BATTALION_ID_, code: item.BATTALION_CODE_, name: item.BATTALION_NAME_ }
        currentContext.tail = { id: item.TAIL_ID_, name: item.TAIL_NO_, unitId: item.TAIL_UNIT_ID_ }
        currentContext.platform = { id: item.PLATFORM_ID_, code: item.PLATFORM_CODE_, name: item.PLATFORM_NAME_ }
        battalionId = item.BATTALION_ID_;
    }
    log.debug('fetchingGetCompaniesByBattalion');
    const companiesResponse = await app.query.findCompaniesByBattalion({ params: { battalionId: battalionId.toString() } });
    log.debug('companiesResponse',companiesResponse);
    if (companiesResponse.data && companiesResponse.data.length > 0) {
        const companies = companiesResponse.data.map(i => ({ id: i.ID_, name: i.NAME_, code: i.CODE_ }));
        currentContext.companies = companies;
    }
    const platformId = (currentContext?.platform?.id ?? 0).toString()
    const callSignsResponse = await app.query.getCallSignFromPlatform({ params: { platformId } })
    log.debug('callSignsResponse',callSignsResponse);
    if (callSignsResponse.data && callSignsResponse.data.length > 0) {
        const callSigns = callSignsResponse.data.map(i => ({ id: i.ID_, code: i.SHORT_NAME_, name: i.NAME_, companyId: i.UNIT_ID }))
        currentContext.callSigns = callSigns;
    }
    const platformConfig = await db.find('ADM_MST_PFC_SNAPSHOT_PLATFORM_CONFIG', { CMD_ID_: currentContext.command.id, PLTF_ID_: currentContext.platform.id });
    currentContext.platformConfig = { id: platformConfig.ID_, engineCount: platformConfig.ENGINE_COUNT_, isApu: platformConfig.IS_APU_ === 1, disableCycleForRecording: platformConfig.DISABLE_CYCLE_FOR_RECORDING_ === 1, enablePassengerCodeRecording: platformConfig.ENABLE_PASSENGER_CODE_RECORDING_ === 1, maxPassengers: platformConfig.MAX_PASSENGERS_, minPilots: platformConfig.MIN_PILOTS_ }

    const unitUsage = await db.find('ADM_TRN_UNIT_USAGE',{UNIT_ID_:currentContext.tail.unitId});
    currentContext.tailStatus = {...currentContext.tailStatus}

    currentContext.tailStatus.cycles = unitUsage.CYCLES_;
    currentContext.tailStatus.hours = unitUsage.TOT_FLIGHT_MINS_;
    currentContext.tailStatus.landings = unitUsage.LANDINGS_;

    app.var.appContext.set(currentContext);
    log.debug('currentContext',currentContext);
})();

const loggedInUser = app.var.loggedInUser.get();
(async () => {
    const totalUsers = await app.query.countTotalUsers({});
    if(totalUsers.error){
        return;
    }
    const hasUsers = totalUsers.data && totalUsers.data.length > 0
    const hasLoggedInUser = loggedInUser !== undefined;
    if(hasUsers){
        if(hasLoggedInUser){
            navigate('app/home');
        }else{
            navigate('app/authenticate');
        }
    }else{
        navigate('app/login');
    }
})();

